describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.footlocker.co.uk/en/women/clothing/jackets/",
        "html": cache.load("https://www.footlocker.co.uk/en/women/clothing/jackets/")
    }

    // Change to be the number of products on the page
    var x = 29;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'adidas Leoflage Windbreaker - Women Jackets';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '79.99',
                url: 'https://www.footlocker.co.uk/en/p/adidas-leoflage-windbreaker-women-jackets-65342?v=317980284883#!searchCategory=women/clothing/jackets',
                image: 'https://runnerspoint.scene7.com/is/image/rpe/317980284883?$large$',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'adidas Stella Mccartney Warm - Women Jackets';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '120.99',
                url: 'https://www.footlocker.co.uk/en/p/adidas-stella-mccartney-warm-women-jackets-5067?v=317978171633#!searchCategory=women/clothing/jackets',
                image: 'https://runnerspoint.scene7.com/is/image/rpe/317978171633?$large$',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.footlocker.co.uk/en/women/clothing/jackets/",
            "html": cache.load("https://www.footlocker.co.uk/en/women/clothing/jackets/")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Women Jackets",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
