    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.fpnyc.com/Toys/"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 33,
                    "pages": 28
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.fpnyc.com/Toys/"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Toys"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.fpnyc.com/Gaming/Board-Games"
        }

        // Change to be the number of products on the page
        var x = 32;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = '10 Minutes to Kill Board Game';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '22.49',
                    url: 'https://www.fpnyc.com/10-Minutes-to-Kill-Board-Game/3770004610006/Gaming/Board-Games/La-Botte-De-Jeu',
                    image: 'https://images-nitrosell-com.akamaized.net/product_images/8/1806/thumb-3770004610006.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'DC Dice Masters Worlds Finest 2 Player Starter Set';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '17.99',
                    url: 'https://www.fpnyc.com/DC-Dice-Masters-Worlds-Finest-2-Player-Starter-Set/634482722343/Gaming/Board-Games/Wiz-Kids',
                    image: 'https://images-nitrosell-com.akamaized.net/product_images/8/1806/thumb-634482722343.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

