var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache');
const expect = require('chai').expect;
var sku = require('../lib/sku');

describe('www.frys.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.frys.com/product/4986951?site=sr:SEARCH:MAIN_RSLT_PG",
            "html" : cache.load("http://www.frys.com/product/4986951?site=sr:SEARCH:MAIN_RSLT_PG")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Siig 7.1-channel Surround Sound USB Audio Adapter",
                    "price": "21.99",
                    "url": "http://www.frys.com/product/4986951?site=sr:SEARCH:MAIN_RSLT_PG",
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://images.frys.com/art/product/300x300/4986951.01.prod.jpg",
                    "country": 'us',
                    "barcode": '662774000480',
                    "sku" : '4986951'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.frys.com/product/8492218;jsessionid=e2-NtE73NHIZ8uQRLLnz9Q__.node4?site=sr:SEARCH:MAIN_RSLT_PG",
            "html" : cache.load("http://www.frys.com/product/8492218;jsessionid=e2-NtE73NHIZ8uQRLLnz9Q__.node4?site=sr:SEARCH:MAIN_RSLT_PG")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Wowwee Roboraptor Blue",
                    "price": "39.69",
                    "url": 'http://www.frys.com/product/8492218;jsessionid=e2-NtE73NHIZ8uQRLLnz9Q__.node4?site=sr:SEARCH:MAIN_RSLT_PG',
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://images.frys.com/art/product/300x300/8492218.01.prod.jpg',
                    "country": 'us',
                    "barcode": '771171180173',
                    "sku": '8492218'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.frys.com/product/8278406?site=sr:SEARCH:MAIN_RSLT_PG",
            "html" : cache.load("http://www.frys.com/product/8278406?site=sr:SEARCH:MAIN_RSLT_PG")
        }

        it('should be false when product is sold out', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Omron Pedometer USB 5M",
                    "price": "39.99",
                    "url": 'http://www.frys.com/product/8278406?site=sr:SEARCH:MAIN_RSLT_PG',
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://images.frys.com/art/product/300x300/8278406.01.prod.jpg',
                    "country": 'us',
                    "barcode": '073796032319',
                    "sku": '8278406'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.frys.com/product/7191882;jsessionid=Rk8mF99ifmuXuDXALg3Oaw__.node4?site=sr:SEARCH:MAIN_RSLT_PG",
            "html" : cache.load("http://www.frys.com/product/7191882;jsessionid=Rk8mF99ifmuXuDXALg3Oaw__.node4?site=sr:SEARCH:MAIN_RSLT_PG")
        }

        it('should be false when product is special order', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Transformers Prime: The Game Nintendo WII",
                    "price": "18.99",
                    "url": 'http://www.frys.com/product/7191882;jsessionid=Rk8mF99ifmuXuDXALg3Oaw__.node4?site=sr:SEARCH:MAIN_RSLT_PG',
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://images.frys.com/art/product/300x300/7191882.01.prod.jpg',
                    "country": 'us',
                    "barcode": '047875843400',
                    "sku": '7191882'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.frys.com/category/Outpost/Audio/Portable+Audio+Cases+&+Covers/",
            "html" : cache.load("http://www.frys.com/category/Outpost/Audio/Portable+Audio+Cases+&+Covers/")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Portable Audio Cases & Covers",
                    "url":"http://www.frys.com/category/Outpost/Audio/Portable+Audio+Cases+&+Covers/"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.frys.com/category/Outpost/Notebooks+and+Tablets/Laptops/",
            "html" : cache.load("https://www.frys.com/category/Outpost/Notebooks+and+Tablets/Laptops/")
        }

        // Change to be the number of products on the page
        var x = 20;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Lenovo T410 14" Refurbished Laptop With Intel Core i5-520m Processor, 4GB Memory, 250GB Hard Drive and Windows 10';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: '821660511350',
                    name: firstProductName,
                    price: '399.00',
                    url: 'http://www.frys.com/product/9340620?site=sr:SEARCH:MAIN_RSLT_PG',
                    image: "https://images.frys.com/art/product/300x300/9340620.01.prod.jpg",
                    country: "us",
                    sku: '9340620'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Dell i5565-A888BLU 15.6" Laptop With AMD FX-9800P Processor, 8GB Memory, 1TB Hard Drive and Windows 10';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: '884116255888',
                    name: lastProductName,
                    price: '579.00',
                    url: 'http://www.frys.com/product/9334180?site=sr:SEARCH:MAIN_RSLT_PG',
                    image: "https://images.frys.com/art/product/300x300/9334180.01.prod.jpg",
                    country: "us",
                    sku: '9334180'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

    describe('sku regex matching', () => {
        it('should only accept a sku that matches the correct pattern', (done) => {
            let goodSkus = [
                '8443448', '8804852', '8804882', '8731531', '8404778'
            ];
            goodSkus.map((thisSku) => {
                let testUrl = 'https://www.frys.com/product/' + thisSku + '?site=sa:Homepage_hot:box1';
                let result = sku(testUrl);
                result.should.equal(thisSku);
            });
            done();
        });

        it('should not accept a sku with an invalid pattern', (done) => {
            let badSkus = [
                '7981810.jsession=', '8804852,id=12', '8804852&n=2'
            ];
            badSkus.map((thisSku) => {
                let testUrl = 'https://www.frys.com/product/' + thisSku + '?site=sa:Homepage_hot:box1';
                let result = sku(testUrl);
                expect(result).to.equal(null);
            });
            done();
        });

    });

});
