    var should = require('should');
    var Structure = require('../lib/structure.js')
    const cache = require('../lib/cache')

    describe('supplierTest', function () {

        describe('Search Page', function () {

            var payload = {
                "pageType": "search",
                "url": "https://www.fun.com/yoda.html",
                "html": cache.load("https://www.fun.com/yoda.html")
            }
            var x = 51
            it('should return' + x + ' number of products', function () {

                const searchPage = Structure(payload)
                //    console.log(searchPage)
                searchPage.length.should.equal(x)


                var first = searchPage.shift()
                first.name.should.be.equal("Star Wars 16\" Yoda Stuffed Figure Backpack");
                first.price.should.be.equal("9.99");
                first.url.should.be.equal("https://www.fun.com/star-wars-yoda-plush-backpack.html");
                first.image.should.be.equal("https://images.fun.com/products/53223/1-21/star-wars-yoda-plush-backpack.jpg")
                first.country.should.be.equal('us');

                var last = searchPage.pop()

                last.name.should.be.equal("Yoda Candy Bowl Decoration");
                last.price.should.be.equal("34.99");
                last.url.should.be.equal("https://www.fun.com/yoda-candy-bowl-decoration.html");
                last.image.should.be.equal("https://images.fun.com/products/9206/1-21/yoda-candy-bowl-decoration.jpg")
                last.country.should.be.equal('us');


            });

        });

        describe('In stock Page', function () {

            var payload = {
                "pageType": "product",
                "url": "https://www.fun.com/star-wars-yoda-plush-backpack.html",
                "html": cache.load("https://www.fun.com/star-wars-yoda-plush-backpack.html")
            }

            it('should be true when product is in stock', function () {

                const inStockPage = Structure(payload)
                // console.log(inStockPage)
                inStockPage.name.should.be.equal("Yoda Star Wars Stuffed Figure 16\" Backpack");
                inStockPage.price.should.be.equal("9.99");
                inStockPage.url.should.be.equal(payload.url);
                inStockPage.inStock.should.be.equal(true);
                should(inStockPage.stockLevel).be.equal(null);
                inStockPage.inStock.should.be.equal(true);
                inStockPage.image.should.be.equal('https://images.fun.com/products/53223/1-2/star-wars-yoda-plush-backpack.jpg');
                inStockPage.country.should.be.equal('us');
            });

        });

        describe('Out Of Stock Page', function () {

            var payload = {
                "pageType": "product",
                "url": "https://www.fun.com/paw-patrol-skye-activity-case.html",
                "html": cache.load("https://www.fun.com/paw-patrol-skye-activity-case.html")
            }

            it('should return false when product is out of stock', function () {

                const inStockPage = Structure(payload)
                inStockPage.name.should.be.equal("Paw Patrol Skye Activity Case");
                inStockPage.price.should.be.equal('18.99');
                inStockPage.url.should.be.equal(payload.url);
                should(inStockPage.stockLevel).be.equal(null);
                inStockPage.inStock.should.be.equal(false);
                inStockPage.image.should.be.equal('https://images.fun.com/products/40068/1-2/paw-patrol-skye-activity-case.jpg');
                inStockPage.country.should.be.equal('us');
            });

        });

    });