var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.gameseek.co.uk/pd/1361982/ps4+precision+control+pack"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Ps4 Precision Control Pack",
                    "price": "3.87",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://s3-eu-west-1.amazonaws.com/gameseek.images/products/380/ps4_precision_control_pack_5_Vidkctepk5jev3_a.jpg',
                    "country": 'uk',
                    "description": 'The Precision Control Pack is the perfect blend of comfort and control. These high quality custom fit accessories are designed to improve accuracy and enhance your gaming experience.... Read More'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.gameseek.co.uk/pd/1274625/disney+infinity+2.0+power+discs"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Disney Infinity 2.0 Power Discs",
                    "price": "3.84",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://s3-eu-west-1.amazonaws.com/gameseek.images/products/380/ps3_Multie98ybuyy0rv_a.jpg',
                    "country": 'uk',
                    "description": 'Disney Infinity 2.0 Power Discs... Read More'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.gameseek.co.uk/directory/video-games/ps4/games"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 30,
                    "pages": 41
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.gameseek.co.uk/directory/video-games/ps4/games"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "videogames"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.gameseek.co.uk/directory/video-games/ps4/games"
        }

        // Change to be the number of products on the page
        var x = 30;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Disney Infinity 2.0 Collectors Edition Avengers Starter Pack';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '29.50',
                    url: 'https://www.gameseek.co.uk/pd/1275939/disney+infinity+2.0+collectors+edition+avengers+starter+pack+',
                    image: 'https://s3-eu-west-1.amazonaws.com/gameseek.images/products/380/https://s3-eu-west-1.amazonaws.com/gameseek.images/products/disney_infinity_20_collectors_edition_avengers_sta_11_VideoGamesa2xxa8quzs_a.jpg',
                    country: 'uk',
                    sku: '1275939'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Call of Duty Advanced Warfare';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'Call of Duty Advanced Warfare',
                    price: '13.40',
                    url: 'https://www.gameseek.co.uk/pd/1268118/call+of+duty+advanced+warfare+',
                    image: 'https://s3-eu-west-1.amazonaws.com/gameseek.images/products/380/callofdutyadvancedwarfare_ps4.jpg',
                    country: 'uk',
                    sku: '1268118'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
