var should = require('should');
var Structure = require('../lib/structure.js')


describe('www.gamestop.com', function() {

    before(function()  {
        this.timeout(5000);
    })


    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.gamestop.com/browse/games/xbox-one?nav=16k,28-xu0,131e0-ffff2418"
        }

        it('should return 11 products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(11);
                done()
            })
        });

        it('last product should be Call of Duty', function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'Call of Duty: Infinite Warfare Legacy Pro Edition - Only at GameStop',
                    price: '119.99',
                    url: 'http://www.gamestop.com/xbox-one/games/call-of-duty-infinite-warfare-legacy-pro-edition-only-at-gamestop/130470',
                    image: 'http://www.gamestop.com/common/images/sbox/125884a.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        it('first product should be Gears of War 4', function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'Gears of War 4',
                    price: '59.99',
                    url: 'http://www.gamestop.com/xbox-one/games/gears-of-war-4/129006',
                    image: 'http://www.gamestop.com/common/images/sbox/1236884a.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.gamestop.com/browse/games/playstation-4?nav=28-xu0,13ffff2418-1dc"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 50,
                    "pages": 23
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.gamestop.com/browse/games/xbox-one?nav=16k,28-xu0,131e0-ffff2418"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Xbox One"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.gamestop.com/ps3/games/call-of-duty-black-ops-iii/122278"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Call of Duty: Black Ops III by Activision",
                    "price": "39.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'www.gamestop.com/common/images/lbox/113302b.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });


    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.gamestop.com/ps4/consoles/playstation-4-limited-edition-star-wars-battlefront-500gb-bundle/125497"
        }

        it('should return false when (new/physical) product is out of stock', function(done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "PlayStation 4 Limited Edition STAR WARS Battlefront 500GB Bundle by Sony Computer Entertainment",
                    "price": "399.99",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'www.gamestop.com/common/images/lbox/118048b1.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })

    describe('Pre order Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.gamestop.com/ps4/games/call-of-duty-infinite-warfare-legacy-pro-edition-only-at-gamestop/130471"
        }

        it('should return false when product is available for pre order', function(done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Call of Duty: Infinite Warfare Legacy Pro Edition - Only at GameStop by Activision",
                    "price": "119.99",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'www.gamestop.com/common/images/lbox/125885brp1.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })

    describe('Pre owned Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.gamestop.com/ps4/consoles/playstation-4-500gb-refurbished-blast-from-the-past-family-bundle/125793"
        }

        it('should return false when product is second hand', function(done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "PlayStation 4 500GB Refurbished Blast from the Past Family Bundle by GameStop Inc.",
                    "price": "349.99",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'www.gamestop.com/common/images/lbox/B909276Ub1.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })


});
