var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.gandermountain.com/modperl/product/details.cgi?pdesc=Plano-113200-SPIRE-Compact-Crossbow-Case&i=962607"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Plano 113200 SPIRE Compact Crossbow Case",
                    "price": "49.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://cdn.gandermountain.com/product/962/962607_L1.jpg?interpolation=lanczos-none&downsize=450px:*',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.gandermountain.com/Archery/Crossbows-Accessories"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 100,
                    "pages": 2
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.gandermountain.com/Guns/Handguns"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Handguns, Pistols & Revolvers"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });
    
    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.gandermountain.com/Archery/Crossbows-Accessories"
        }

        // Change to be the number of products on the page
        var x = 32;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Plano 113200 SPIRE Compact Crossbow Case';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '49.99',
                    url: 'www.gandermountain.com/modperl/product/details.cgi?pdesc=Plano-113200-SPIRE-Compact-Crossbow-Case&i=962607',
                    image: 'http://cdn.gandermountain.com/product/962/962607_L1.jpg?interpolation=lanczos-none&downsize=175px:*&composite=0,0',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Carbon Express Covert CX-3 SL+ Ready-To-Hunt Crossbow Kit';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '699.99',
                    url: 'www.gandermountain.com/modperl/product/details.cgi?pdesc=Carbon-Express-Covert-CX-3-SL-Ready-Hunt-Crossbow-Kit&i=873678',
                    image: 'http://cdn.gandermountain.com/product/873/873678_L1.png?interpolation=lanczos-none&downsize=175px:*',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
