var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.gardeners.com/buy/wicker-love-seat-cushion-sale/33-419L.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Wicker Loveseat Cushion Sale",
                    "price": "26.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://demandware.edgesuite.net/sits_pod32/dw/image/v2/AABF_PRD/on/demandware.static/-/Sites-GSC_Products/default/dw96078715/Products/33419_248.jpg?sw=374&sh=488",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.gardeners.com/buy/gardeners-supply-outlet/outdoor-cushions-sale/"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Cushion & Furniture Outlet"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.gardeners.com/buy/garden-tools/garden-kneelers/"
        }

        // Change to be the number of products on the page
        var x = 16;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Deep-Seat Garden Kneeler';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '29.89',
                    url: 'http://www.gardeners.com/buy/deep-seat-garden-kneeler/40-009.html',
                    image: 'http://www.gardeners.com/dw/image/v2/AABF_PRD/on/demandware.static/-/Sites-GSC_Products/default/dw2d4230fe/Products/40009_5553_deep-seat-garden-kneeler.jpg?sw=182&sh=237',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Rose Print Garden Glove and Kneeling Pad Set';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '52.99',
                    url: 'http://www.gardeners.com/buy/rose-print-gloves-and-kneeling-pad-gardening-gift-set/8593475.html',
                    image: 'http://www.gardeners.com/dw/image/v2/AABF_PRD/on/demandware.static/-/Sites-GSC_Products/default/dw50cdcf14/Products/8593475_6861_cute-gardening-gloves-and-kneeler-gift-for-gardener.jpg?sw=182&sh=237',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
