var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.gear4music.com/Recording-and-Computers/Beyerdynamic-DTX-11-iE-In-Ear-Headphones-Sporty-Green/K2F",
            "html" :cache.load("http://www.gear4music.com/Recording-and-Computers/Beyerdynamic-DTX-11-iE-In-Ear-Headphones-Sporty-Green/K2F")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Beyerdynamic DTX 11 iE In Ear Headphones, Sporty Green",
                    "price": "14.00",
                    "url": payload.url,
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": 'http://cdns3.gear4music.com/media/4/43771/317/preview.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.gear4music.com/Home-Audio/In-Ear-Headphones",
            "html" :cache.load("http://www.gear4music.com/Home-Audio/In-Ear-Headphones")
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "In-ear Headphones",
                    "url":"http://www.gear4music.com/Home-Audio/In-Ear-Headphones"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.gear4music.com/Home-Audio/In-Ear-Headphones",
            "html" :cache.load("http://www.gear4music.com/Home-Audio/In-Ear-Headphones")
        }

        // Change to be the number of products on the page
        var x = 46;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Beyerdynamic DTX 11 iE In Ear Headphones, Sporty Green';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '14.00',
                    url: 'http://www.gear4music.com/Recording-and-Computers/Beyerdynamic-DTX-11-iE-In-Ear-Headphones-Sporty-Green/K2F',
                    image: 'http://cdns3.gear4music.com/media/4/43771/98/preview.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Shure SE846 Sound Isolating Earphones, Black';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '899.00',
                    url: 'http://www.gear4music.com/PA-DJ-and-Lighting/Shure-SE846-Sound-Isolating-Earphones-Black/1F3X',
                    image: 'http://cdns3.gear4music.com/media/15/158811/98/preview.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
