describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.ghbass.com/category/g+h+bass/women/boots+%26+booties.do",
        "html": cache.load("https://www.ghbass.com/category/g+h+bass/women/boots+%26+booties.do")
    }

    // Change to be the number of products on the page
    var x = 44;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Nadine Hiker Boot Olive/Tobacco';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '135.00',
                url: 'https://www.ghbass.com/product/nadine+hiker+boot.do?sortby=ourPicks&from=fn&selectedOption=547501',
                image: 'https://ii.ghbass.com/fcgi-bin/iipsrv.fcgi?FIF=/images/ghbass/source/6154-3624_61543624c55oltob.tif&qlt=100&wid=650&cvt=jpeg',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Sylvia Bootie Cloud Grey';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '130.00',
                url: 'https://www.ghbass.com/product/sylvia+bootie.do?sortby=ourPicks&from=fn&selectedOption=208857',
                image: 'https://ii.ghbass.com/fcgi-bin/iipsrv.fcgi?FIF=/images/ghbass/source/6160-3559_61603559c03ll.tif&qlt=100&wid=650&cvt=jpeg',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.ghbass.com/category/g+h+bass/women/boots+%26+booties.do",
            "html": cache.load("https://www.ghbass.com/category/g+h+bass/women/boots+%26+booties.do")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Boots & Booties",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
