var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.gianttiger.com/product/777203.do?sortby=newArrivals&refType=&from=fn"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "NAT's Best Sellers Polar Fleece Work Shirt - Red",
                    "price": "29.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://ii.gianttiger.com/fcgi-bin/iipsrv.fcgi?FIF=/images/gianttiger/source/products/777203/E777203_fr01.tif&wid=480&cvt=jpeg',
                    "country": 'us',
                    "description": 'Size: S - 3XL Colour: Red and Black Material: 100% polyester Brushed polar fleece Comfortable, light, warm and breathable Full cut Wrist and chest pockets with buttons'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.gianttiger.com/product/777221.do?sortby=newArrivals&refType=&from=fn"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "NAT's Best Sellers Padded Work Shirt -Blue",
                    "price": null,
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://ii.gianttiger.com/fcgi-bin/iipsrv.fcgi?FIF=/images/gianttiger/source/products/777221/E777221_fr01.tif&wid=480&cvt=jpeg',
                    "country": 'us',
                    "description": 'Size: S - 3XL Colour: Grey and Blue Material: 100% polyester Brushed cotton shell Quilted lining Front zipper Polar facing and hood Snap buttons flap'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.gianttiger.com/category/departments/men/shirts.do"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 15,
                    "pages": 5
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.gianttiger.com/category/departments/men/shirts.do"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Shirts"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.gianttiger.com/category/departments/men/shirts.do"
        }

        // Change to be the number of products on the page
        var x = 15;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Men\'s Long Sleeve Printed T-Shirt';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: null,
                    url: 'https://www.gianttiger.com/product/790728.do?sortby=newArrivals&refType=&from=fn',
                    image: 'https://ii.gianttiger.com/fcgi-bin/iipsrv.fcgi?FIF=/images/gianttiger/source/products/790728/E790728_fr01.tif&qlt=75&wid=240&cvt=jpeg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Mountain Ridge Men\'s Pocket T-Shirt';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: null,
                    url: 'https://www.gianttiger.com/product/710308.do?sortby=newArrivals&refType=&from=fn',
                    image: 'https://ii.gianttiger.com/fcgi-bin/iipsrv.fcgi?FIF=/images/gianttiger/source/products/765814/E765814_gc.tif&qlt=75&wid=240&cvt=jpeg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
