describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.globalgolf.com/golf-apparel/",
        "html": cache.load("https://www.globalgolf.com/golf-apparel/")
    }

    // Change to be the number of products on the page
    var x = 16;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Classic Pro-Fit Pants';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '29.99',
                url: 'https://www.globalgolf.com/golf-apparel/1033408-greg-norman-classic-pro-fit-pants/closeout/',
                image: 'https://cdn0.globalgolf.com/images/product/small/1033000/1033408-0-1-7-82-991-0-0-6-19.jpg',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Chest Print Shirt';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '39.99',
                url: 'https://www.globalgolf.com/golf-apparel/1044319-adidas-chest-print-shirt/closeout/',
                image: 'https://cdn0.globalgolf.com/images/product/small/1044000/1044319-0-1-7-95-8-0-0-6-19.jpg',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.globalgolf.com/golf-apparel/",
            "html": cache.load("https://www.globalgolf.com/golf-apparel/")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Golf Apparel",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
