var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.gordmans.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.gordmans.com/toys/preschool/puppys-smart-stages-driver-110445093.html#start=3"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Puppy's Smart Stages Driver",
                    "price": "29.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://demandware.edgesuite.net/aawc_prd/on/demandware.static/-/Sites-itemmaster_gordmans/en_US/dwfd904f5f/images/110445093-Puppy s Smart Stages Driver-1.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });


    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.gordmans.com/men/mens-clothing/"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 60,
                    "pages": 4
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.gordmans.com/toys/"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Toys & Games"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.gordmans.com/toys/preschool/"
        }

        // Change to be the number of products on the page
        var x = 12;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Sis\' Remote';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '9.99',
                    url: 'http://www.gordmans.com/toys/preschool/sis-remote-110445156.html',
                    image: 'http://demandware.edgesuite.net/sits_pod29/dw/image/v2/AAWC_PRD/on/demandware.static/-/Sites-itemmaster_gordmans/en_US/dw8aea690a/images/110445156-Sis Remote-1.jpg?sw=500&sh=500&sm=fit',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'First Builders Fire Truck';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '9.99',
                    url: 'http://www.gordmans.com/toys/preschool/first-builders-fire-truck-110266450.html',
                    image: 'http://demandware.edgesuite.net/sits_pod29/dw/image/v2/AAWC_PRD/on/demandware.static/-/Sites-itemmaster_gordmans/en_US/dw73e3e16d/images/110266450-Mega First Builders Fire Truck-1.jpg?sw=500&sh=500&sm=fit',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
