var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('HABITAT', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.habitat.co.uk/tico-405040",
            "html": cache.load("https://www.habitat.co.uk/tico-405040")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "TICO 8 seater Tico dining set with ecru table and 2 benches",
                    "price": "1395.00",
                    "url": 'https://www.habitat.co.uk/tico-405040',
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://cdn.habitat.co.uk/skin/frontend/habitat/base/images/SVG/logo.svg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.habitat.co.uk/herrmann-square-glass-coffee-table-449165",
            "html": cache.load("https://www.habitat.co.uk/herrmann-square-glass-coffee-table-449165")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "HERRMANN Square glass coffee table",
                    "price": "295.00",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://www.habitat.co.uk/design/content/assets/images/dotmailer/close-icon.png",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.habitat.co.uk/garden/shop-by-category/garden-furniture",
        "html": cache.load("https://www.habitat.co.uk/garden/shop-by-category/garden-furniture")
    }

    // Change to be the number of products on the page
    var x = 30;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'TICO 8 seater Tico dining set with ecru table and 2 benches';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '1395.00',
                url: 'https://www.habitat.co.uk/tico-405040',
                image: 'https://cdn.habitat.co.uk/media/catalog/product/cache/1/small_image/153x/9df78eab33525d08d6e5fb8d27136e95/4/0/405040.jpg',
                country: 'uk'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'JAMBI Green and black woven garden bench';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '250.00',
                url: 'https://www.habitat.co.uk/jambi-green-and-black-woven-garden-bench-778392',
                image: 'https://cdn.habitat.co.uk/media/catalog/product/cache/1/small_image/153x/9df78eab33525d08d6e5fb8d27136e95/7/7/778392.jpg',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "http://www.tesco.com/direct/home-electrical/electrical-accessories/cat3376649.cat?icid=homeelectrical_flyoutlink_BatteriesElectricalAccessories",
            "html": cache.load("http://www.tesco.com/direct/home-electrical/electrical-accessories/cat3376649.cat?icid=homeelectrical_flyoutlink_BatteriesElectricalAccessories")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "",
                    "url": "https://www.habitat.co.uk/garden/shop-by-category/garden-furniture#"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});


