var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.halfpriceperfumes.co.uk/en/10-20-clearance-sale/111557-lambretta-italia-50ml-eau-de-toilette-gift-set-for-him.html",
            "html" : cache.load("https://www.halfpriceperfumes.co.uk/en/10-20-clearance-sale/111557-lambretta-italia-50ml-eau-de-toilette-gift-set-for-him.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Lambretta Italia 50ml Eau De Toilette Gift Set for Him",
                    "price": "10.00",
                    "url": payload.url,
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": 'www.halfpriceperfumes.co.uk/img/p/1/7/9/6/9/5/179695-large.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.halfpriceperfumes.co.uk/en/3515-gift-sets-for-him",
            "html" : cache.load("https://www.halfpriceperfumes.co.uk/en/3515-gift-sets-for-him")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 12,
                    "pages": 2,
                    "url" : "https://www.halfpriceperfumes.co.uk/en/3515-gift-sets-for-him"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.halfpriceperfumes.co.uk/en/3515-gift-sets-for-him",
            "html" : cache.load("https://www.halfpriceperfumes.co.uk/en/3515-gift-sets-for-him")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Gift Sets For Him",
                    "url" : "https://www.halfpriceperfumes.co.uk/en/3515-gift-sets-for-him"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

     describe('Not a search page', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.halfpriceperfumes.co.uk/en/10-20-clearance-sale/111548-carrera-men-30ml-eau-de-toilette-gift-set-for-him.html",
            "html" : cache.load("https://www.halfpriceperfumes.co.uk/en/10-20-clearance-sale/111548-carrera-men-30ml-eau-de-toilette-gift-set-for-him.html")
        }

        it('should return null items and pages', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null,
                    "url" : "https://www.halfpriceperfumes.co.uk/en/10-20-clearance-sale/111548-carrera-men-30ml-eau-de-toilette-gift-set-for-him.html"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });


    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.halfpriceperfumes.co.uk/en/3509-10-aftershaves",
            "html" : cache.load("https://www.halfpriceperfumes.co.uk/en/3509-10-aftershaves")
        }

        // Change to be the number of products on the page
        var x = 12;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Lambretta Italia 50ml Eau De Toilette Gift Set for Him';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '10.00',
                    url: 'https://www.halfpriceperfumes.co.uk/en/10-20-clearance-sale/111557-lambretta-italia-50ml-eau-de-toilette-gift-set-for-him.html',
                    image: 'www.halfpriceperfumes.co.uk/img/p/1/7/9/6/9/5/179695-home.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Baylis & Harding Black Pepper & Ginseng Football Wash Bag';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '14.99',
                    url: 'https://www.halfpriceperfumes.co.uk/en/gift-sets-for-him/114473-baylis-harding-black-pepper-ginseng-football-wash-bag-017854078031.html',
                    image: 'www.halfpriceperfumes.co.uk/img/p/1/8/3/4/5/7/183457-home.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
