var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.harrods.com/product/hugh-christmas-bear-2016/harrods/000000000005124891?cat1=new-nav-children&cat2=new-gifts-toys-all",
            "html": cache.load("http://www.harrods.com/product/hugh-christmas-bear-2016/harrods/000000000005124891?cat1=new-nav-children&cat2=new-gifts-toys-all")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Harrods Hugh Christmas Bear 2016",
                    "price": "24.95",
                    "url": payload.url,
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": 'http://images.harrods.com/img/5124891?$fullScreen$',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.harrods.com/children/toys?icid=megamenu_GF_luxury_gifts_toys",
            "html": cache.load("http://www.harrods.com/children/toys?icid=megamenu_GF_luxury_gifts_toys")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Toys",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.harrods.com/children/toys?icid=megamenu_GF_luxury_gifts_toys",
            "html": cache.load("http://www.harrods.com/children/toys?icid=megamenu_GF_luxury_gifts_toys")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 18,
                    "pages": 51,
                    "url" : "http://www.harrods.com/children/toys?icid=megamenu_GF_luxury_gifts_toys"
                })
                JSON.stringify(err, pagination).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.harrods.com/en-gb/homewares/kitchenware/home-appliances?icid=megamenu_homewares_kitchenware_home-appliances",
            "html": cache.load("https://www.harrods.com/en-gb/homewares/kitchenware/home-appliances?icid=megamenu_homewares_kitchenware_home-appliances")
        }

        // Change to be the number of products on the page
        var x = 36;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Dyson V8 Animal Cordless Vacuum Cleaner';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '349.99',
                    url: 'http://www.harrods.com/en-gb/dyson/v8-animal-cordless-vacuum-cleaner-p000000000005295159',
                    image: 'http://images.harrods.com/product/dyson/v8-animal-cordless-vacuum-cleaner_000000000005295159.jpg?dwn=520px:592px',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Laurastar X-Treme Ironing Board Cover';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '199.00',
                    url: 'http://www.harrods.com/en-gb/laurastar/x-treme-ironing-board-cover-p000000000003857433',
                    image: 'http://images.harrods.com/product/laurastar/x-treme-ironing-board-cover_000000000003857433.jpg?dwn=520px:592px',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
