var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')


describe('replaceWithwebAddress', function () {

    this.timeout(10000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.hayneedle.com/product/joraform106gal400composttumbler.cfm",
            "html": cache.load("http://www.hayneedle.com/product/joraform106gal400composttumbler.cfm")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Jora 106 gal. 400 Compost Tumbler",
                    "price": "799.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://images.hayneedle.com/mgen/master:TCW003.jpg?is=70,70,0xffffff',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function () {

        var payload = {
            "pageType": "pagination",
            "url": "http://www.hayneedle.com/baby-and-kids/learning-and-educational-toys_suggested-age-birth-to-18-months_list_500879_284?categoryId=500879&selectedFacets=&page=1&sortBy=preferred:desc&checkCache=true&pageType=PRODUCT_CATEGORY&view=48",
            "html": cache.load("http://www.hayneedle.com/baby-and-kids/learning-and-educational-toys_suggested-age-birth-to-18-months_list_500879_284?categoryId=500879&selectedFacets=&page=1&sortBy=preferred:desc&checkCache=true&pageType=PRODUCT_CATEGORY&view=48")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, pagination) {
                var expected = JSON.stringify({
                    "items": 48,
                    "pages": 6,
                    "url": "http://www.hayneedle.com/baby-and-kids/learning-and-educational-toys_suggested-age-birth-to-18-months_list_500879_284?categoryId=500879&selectedFacets=&page=1&sortBy=preferred:desc&checkCache=true&pageType=PRODUCT_CATEGORY&view=48"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "http://www.hayneedle.com/furniture/bedroom-furniture_183822",
            "html": cache.load("http://www.hayneedle.com/furniture/bedroom-furniture_183822")
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Bedroom Furniture",
                    "url": "http://www.hayneedle.com/furniture/bedroom-furniture_183822"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.hayneedle.com/brands/10-strawberry-street/list",
            "html": cache.load("https://www.hayneedle.com/brands/10-strawberry-street/list")
        }

        // Change to be the number of products on the page
        var x = 48;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = '10 Strawberry Street Caterers Set Round Salad/Dessert Plate Set of 12 - 7.5 in.';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '18.04',
                    url: 'https://www.hayneedle.com/product/10strawberrystreetcatererssetroundsaladdessertplatesetof1275in.cfm',
                    image: 'https://images.hayneedle.com/mgen/master:TST242.jpg?is=300,300,0xffffff&cvt=jpg',
                    country: 'us',
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = '10 Strawberry Street Creek Deep Bowl';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '35.99',
                    url: 'https://www.hayneedle.com/product/10-strawberry-street-creek-deep-bowl.cfm',
                    image: 'https://images.hayneedle.com/mgen/master:TST362.jpg?is=300,300,0xffffff&cvt=jpg',
                    country: 'us',
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
