var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.herbergers.com/sc1/women/womens-jeans/karen-kane-vintage-wash-jeggings-1161727.html",
            "html" : cache.load("http://www.herbergers.com/sc1/women/womens-jeans/karen-kane-vintage-wash-jeggings-1161727.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Karen Kane® Vintage Wash Jeggings",
                    "price": "118.00",
                    "url": "http://www.herbergers.com/sc1/women/womens-jeans/karen-kane-vintage-wash-jeggings-1161727.html",
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://s7d4.scene7.com/is/image/BonTon/1161727_nocolor?fit=constrain,1&wid=500&hei=500&fmt=jpeg&op_sharpen=1&resMode=sharp2',
                    "country": 'us',
                    "description": 'Get comfort and classic style with this jean legging from Karen Kane that features a vintage wash for a casual look. Cotton / polyester / spandex Stretch, solid waistband Faux front pockets Vintage wash details Slim leg fit Imported Additional information Shipping & Delivery Details Returning a purchase Customer Service'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    /*describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : ""
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });*/

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.herbergers.com/sc1/women/womens-jeans/#ctx=pageNum%3A1%26pageSize%3A360&pos=0",
            "html" : cache.load("http://www.herbergers.com/sc1/women/womens-jeans/#ctx=pageNum%3A1%26pageSize%3A360&pos=0")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 360,
                    "pages": 2,
                    "url" : payload.url
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.herbergers.com/sc1/women/womens-jeans/#ctx=pageNum%3A1%26pageSize%3A360&pos=0",
            "html" : "http://www.herbergers.com/sc1/women/womens-jeans/#ctx=pageNum%3A1%26pageSize%3A360&pos=0"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.herbergers.com/sc1/women/womens-jeans/#ctx=pageNum%3A1%26pageSize%3A360&pos=0",
            "html" : cache.load("https://www.herbergers.com/sc1/women/womens-jeans/#ctx=pageNum%3A1%26pageSize%3A360&pos=0")
        }

        // Change to be the number of products on the page
        var x = 360;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Silver Jeans Co. Izzy Raw Hem Ankle Jeans';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '79.20',
                    url: 'http://www.herbergers.com/sc1/women/womens-jeans/silver-jeans-co-izzy-raw-hem-ankle-jeans-1303554.html',
                    image: 'https://s7d4.scene7.com/is/image/BonTon/1303554?$medium_245$',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Poetic Justice® Maya Mid-Rise Skinny Jeans';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '42.00',
                    url: 'http://www.herbergers.com/sc1/women/womens-jeans/poetic-justice-maya-mid-rise-skinny-jeans-979769.html',
                    image: 'https://s7d4.scene7.com/is/image/BonTon/979769?$medium_245$',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
