var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.hifigear.co.uk/sennheiser-mx-475-in-ear-headphones.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Sennheiser MX 475 In-Ear Headphones",
                    "price": "19.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://www.hifigear.co.uk/media/catalog/product/cache/1/image/318x318/9df78eab33525d08d6e5fb8d27136e95/2/3/233862295.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.hifigear.co.uk/headphones/in-ear-headphones.html"
        }

        // Change to be the number of products on the page
        var x = 9;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Sennheiser MX 475 In-Ear Headphones';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '19.00',
                    url: 'http://www.hifigear.co.uk/sennheiser-mx-475-in-ear-headphones.html',
                    image: 'http://www.hifigear.co.uk/media/catalog/product/cache/1/small_image/210x/9df78eab33525d08d6e5fb8d27136e95/2/3/233862295.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })
        describe('Search page category', function() {

            var payload = {
                "pageType" : "category",
                "url" : "http://www.hifigear.co.uk/headphones/in-ear-headphones.html"
            }

            it('should be true when number of items and pages correct', function (done) {

                Structure(payload, function(category) {
                    var expected = JSON.stringify({
                        "name": "In-Ear Headphones"
                    })
                    JSON.stringify(category).should.equal(expected);
                    done()
                })
            });

        });
        describe('Search page pagination', function() {

            var payload = {
                "pageType" : "pagination",
                "url" : "http://www.hifigear.co.uk/headphones/in-ear-headphones.html"
            }

            it('should be true when number of items and pages correct', function (done) {

                Structure(payload, function(pagination) {
                    var expected = JSON.stringify({
                        "items": 9,
                        "pages": 3
                    })
                    JSON.stringify(pagination).should.equal(expected);
                    done()
                })
            });

        });

         describe('Not a search page', function() {

            var payload = {
                "pageType" : "pagination",
                "url" : "http://www.hifigear.co.uk/sennheiser-cx-5-00i-in-ear-headphones.html"
            }

            it('should return null items and pages', function (done) {

                Structure(payload, function(pagination) {
                    var expected = JSON.stringify({
                        "items": null,
                        "pages": null
                    })
                    JSON.stringify(pagination).should.equal(expected);
                    done()
                })
            });

        });

        var lastProductName = 'Audio-Technica ATH-ANC33iS';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '64.00',
                    url: 'http://www.hifigear.co.uk/audio-technica-ath-anc33is.html',
                    image: 'http://www.hifigear.co.uk/media/catalog/product/cache/1/small_image/210x/9df78eab33525d08d6e5fb8d27136e95/a/t/ath-anc33is_568x350.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
