var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function () {

    this.timeout(10000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.hobbycraft.co.uk/galt-water-magic-farm/574155-1000",
            "html": cache.load("http://www.hobbycraft.co.uk/galt-water-magic-farm/574155-1000")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Galt Water Magic Farm",
                    "price": "6.00",
                    "url": payload.url,
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": 'www.hobbycraft.co.uk/supplyimages/574155_1000_1_340.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function () {

        var payload = {
            "pageType": "pagination",
            "url": "http://www.hobbycraft.co.uk/new-arrivals",
            "html": cache.load("http://www.hobbycraft.co.uk/new-arrivals")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, pagination) {
                var expected = JSON.stringify({
                    "items": 16,
                    "pages": 5,
                    "url": "http://www.hobbycraft.co.uk/new-arrivals"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Not a search page', function () {

        var payload = {
            "pageType": "pagination",
            "url": "http://www.hobbycraft.co.uk/spring-foam-sticker-tub/622303-1000",
            "html": cache.load("http://www.hobbycraft.co.uk/spring-foam-sticker-tub/622303-1000")
        }

        it('should be true when pages and items are null', function (done) {

            Structure(payload, function (err, pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null,
                    "url": "http://www.hobbycraft.co.uk/spring-foam-sticker-tub/622303-1000"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "http://www.hobbycraft.co.uk/art-supplies/drawing/art-pencils?page=1",
            "html": cache.load("http://www.hobbycraft.co.uk/art-supplies/drawing/art-pencils?page=1")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Art Pencils",
                    "url": "http://www.hobbycraft.co.uk/art-supplies/drawing/art-pencils?page=1"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.hobbycraft.co.uk/craft-essentials/craft-blanks-and-bases/mache-shapes-and-boxes",
            "html": cache.load("https://www.hobbycraft.co.uk/craft-essentials/craft-blanks-and-bases/mache-shapes-and-boxes")
        }

        // Change to be the number of products on the page
        var x = 100;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Hanging Mache Egg 10 cm';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '0.50',
                    url: 'https://www.hobbycraft.co.uk/hanging-mache-egg-10-cm/622294-1000',
                    image: 'http://www.hobbycraft.co.uk/supplyimages/622294_1000_1_170.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Decopatch Mache Butterfly Mask 8 x 23.5 x 19.5 cm';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '3.75',
                    url: 'https://www.hobbycraft.co.uk/decopatch-mache-butterfly-mask-8-x-235-x-195-cm/610338-1000',
                    image: 'http://www.hobbycraft.co.uk/supplyimages/610338_1000_1_170.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
