var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.holabirdsports.com/running/clothing/men-s/asics-3-split-shorts-mens-1.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "ASICS 3\" Split Shorts Men\'s",
                    "price": "24.95",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://cdn.media.holabirdsports.com/media/catalog/product/cache/1/image/260x260/17f82f742ffe127f42dca9de82fb58b1/0/5/054477_904_1.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.holabirdsports.com/running/clothing/men-s.html"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 100,
                    "pages": 1
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.holabirdsports.com/racquet-sports/squash/racquets.html"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "All Squash Racquets"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.holabirdsports.com/running/clothing/men-s.html"
        }

        // Change to be the number of products on the page
        var x = 16;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = "ASICS 3\" Split Shorts Men's";
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '24.95',
                    url: 'http://www.holabirdsports.com/running/clothing/men-s/asics-3-split-shorts-mens-1.html',
                    image: 'http://cdn.media.holabirdsports.com/media/catalog/product/cache/1/small_image/116x115/9df78eab33525d08d6e5fb8d27136e95/0/5/054477_904_1.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = "Saucony Run Lux Short Men's Fall 2016";
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '40.95',
                    url: 'http://www.holabirdsports.com/running/clothing/men-s/saucony-run-lux-short-mens-fall-2016.html',
                    image: 'http://cdn.media.holabirdsports.com/media/catalog/product/cache/1/small_image/116x115/9df78eab33525d08d6e5fb8d27136e95/0/5/055018_bbk_1.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
