var should = require('should');
var Structure = require('../lib/structure.js')


describe('www.homebargains.co.uk', function() {

    before(function()  {
        this.timeout(5000); 
    })


    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.homebargains.co.uk/category/2-toys-games.aspx"
        }

        it('should return 40 products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(40);
                done()
            })
        });

        it('last product should be a LeapFrog My Discovery House', function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'LeapFrog My Discovery House',
                    price: '9.99',
                    url: 'http://www.homebargains.co.uk/products/11261-leapfrog-my-discovery-house.aspx',
                    image: 'http://www.homebargains.co.uk/Handlers/ImageHandler.ashx?id=26389&targetSize=150',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        it('first product should be Angry Birds Key Chain (Set of 4)', function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'Angry Birds Key Chain (Set of 4)',
                    price: '11.96',
                    url: 'http://www.homebargains.co.uk/products/12218-angry-birds-key-chain-set-of-4.aspx',
                    image: 'http://www.homebargains.co.uk/Handlers/ImageHandler.ashx?id=28533&targetSize=150',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

    });


    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.homebargains.co.uk/products/12218-angry-birds-key-chain-set-of-4.aspx"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Angry Birds Key Chain (Set of 4)",
                    "price": "11.96",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://www.homebargains.co.uk/Handlers/ImageHandler.ashx?id=28533&targetSize=300",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.homebargains.co.uk/category/2-toys-games.aspx"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Toys & Games"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });
    describe('Checking to see if no pgination works', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.homebargains.co.uk/"
        }

        it('should return 0/null items and null pages', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });
    });
    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.homebargains.co.uk/category/2-toys-games.aspx"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 40,
                    "pages": 5
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });


    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.homebargains.co.uk/products/11093-ecoiffier-100-tea-time-chef.aspx"
        }

        it('should return false when product is unavailable for delivery', function(done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Ecoiffier 100% Tea Time Chef",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "http://www.homebargains.co.uk/Handlers/ImageHandler.ashx?id=26022&targetSize=300",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })

});
