var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.homeessentials.co.uk', function() {

    this.timeout(10000);


        describe('In stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.homeessentials.co.uk/shop/marvel-spider-man-super-web-slinger/zf263/product/details/show.action?pdBoUid=7210#colour:,size:",
            "html" : cache.load("https://www.homeessentials.co.uk/shop/marvel-spider-man-super-web-slinger/zf263/product/details/show.action?pdBoUid=7210#colour:,size:")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Marvel Spider-Man Super Web Slinger",
                    "price": "25.00",
                    "url": payload.url,
                    "inStock": true,                    
                    "stockLevel": null,
                    "image": "https://images.drct2u.com:443/repo/IHM/desktop/header/02-images/160721_IHM_Header_SignIn.png",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

 describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.homeessentials.co.uk/shop/action-toys/toys-nursery/1/_/N-1ythtdg/products/show.action?type=cmr&hnid=1917812785&cm_sp=IHM-ToysandNursery-_-TOYS-_-ActionToys",
            "html" : cache.load("https://www.homeessentials.co.uk/shop/action-toys/toys-nursery/1/_/N-1ythtdg/products/show.action?type=cmr&hnid=1917812785&cm_sp=IHM-ToysandNursery-_-TOYS-_-ActionToys")
        }

        // Change to be the number of products on the page
        var x = 36;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Marvel Spider-Man Super Web Slinger';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '25.00',
                    url: 'https://www.homeessentials.co.uk/shop/marvel-spider-man-super-web-slinger/zf263/product/details/show.action?pdBoUid=7210',
                    image: 'https://productimages.drct2u.com/plp_full_width_1/products/zf/zf263/o16zf263500s.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Batman Voice Changing Helmet';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '43.00',
                    url: 'https://www.homeessentials.co.uk/shop/justice-league-batman-voice-changing-tactical-helmet/bf529/product/details/show.action?pdBoUid=7210',
                    image: '',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});