var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.hottopic.com', function () {

    this.timeout(10000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.hottopic.com/product/funko-ufc-pop-bj-penn-vinyl-figure/10659441.html?cgid=funko",
            "html": cache.load("http://www.hottopic.com/product/funko-ufc-pop-bj-penn-vinyl-figure/10659441.html?cgid=funko")


        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Funko UFC Pop! BJ Penn Vinyl Figure",
                    "price": "10.00",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://hottopic.scene7.com/is/image/HotTopic/10659440_hi?$pdp_hero_standard$',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function () {

        var payload = {
            "pageType": "pagination",
            "url": "http://www.hottopic.com/pop-culture/collectibles/figures-and-toys/",
            "html": cache.load("http://www.hottopic.com/pop-culture/collectibles/figures-and-toys/")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, pagination) {
                var expected = JSON.stringify({
                    "items": 120,
                    "pages": 8,
                    "url": "http://www.hottopic.com/pop-culture/collectibles/figures-and-toys/"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "http://www.hottopic.com/pop-culture/collectibles/figures-and-toys/",
            "html": cache.load("http://www.hottopic.com/pop-culture/collectibles/figures-and-toys/")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Figures And Toys",
                    "url": "http://www.hottopic.com/pop-culture/collectibles/figures-and-toys/"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.hottopic.com/funko/marvel/",
            "html": cache.load("https://www.hottopic.com/funko/marvel/")
        }

        // Change to be the number of products on the page
        var x = 14;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Funko Guardians of the Galaxy: The Telltale Series Pop! Games Gamora Vinyl Bobble-Head';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '9.00',
                    url: 'https://www.hottopic.com/product/funko-guardians-of-the-galaxy-the-telltale-series-pop-games-gamora-vinyl-bobble-head/11194008.html?cgid=funko-marvel',
                    image: 'https://hottopic.scene7.com/is/image/HotTopic/11194007_hi?$tile_clp_3up_standard$',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Funko Marvel Pop! Deadpool With Swords Vinyl Bobble-Head';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '9.00',
                    url: 'https://www.hottopic.com/product/funko-marvel-pop-deadpool-with-swords-vinyl-bobble-head/10470385.html?cgid=funko-marvel',
                    image: 'https://hottopic.scene7.com/is/image/HotTopic/10470384_hi?$tile_clp_3up_standard$',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
