var should = require('should');
var Structure = require('../lib/structure.js')
let cache = require('../lib/cache')

describe('www.houseoffraser.co,uk', function () {
    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.houseoffraser.co.uk/Kenwood+Scene+2+slice+toaster/223008875,default,pd.html",
            "html": cache.load("http://www.houseoffraser.co.uk/Kenwood+Scene+2+slice+toaster/223008875,default,pd.html")
        }

        it('should be true when product is in stock', function () {
            
            const inStockPage =  Structure(payload)            
            inStockPage.name.should.be.equal("Money Maze Bank");
            inStockPage.price.should.be.equal("14.99");
            inStockPage.url.should.be.equal("https://www.bitsandpieces.com/product/money-maze-bank");
            inStockPage.inStock.should.be.equal(true);
            should(inStockPage.stockLevel).be.equal(null);
            inStockPage.inStock.should.be.equal(true);
            inStockPage.image.should.be.equal('https://s3.amazonaws.com/cdn.bitsandpieces.com/images/395/42768.jpg');
            inStockPage.country.should.be.equal('us');            
         });
 
    });
    describe('Search page pagination', function () {

        var payload = {
            "pageType": "pagination",
            "url": "http://www.houseoffraser.co.uk/Electricals/S10,default,sc.html?cm_sp=Homepage-_-SaleMain-_-Link4",
            "html": cache.load("http://www.houseoffraser.co.uk/Electricals/S10,default,sc.html?cm_sp=Homepage-_-SaleMain-_-Link4")
        }

        it('should be true when number of items and pages correct', function () {

            var expected = JSON.stringify({
                "items": 7,
                "pages": null,
                "url":"https://www.bitsandpieces.com/category/childrens-puzzles"
            })

            const pagination = Structure(payload)

            JSON.stringify(pagination).should.equal(expected);
        });


    });

    describe('Checking to see if no pgination works', function () {

        var payload = {
            "pageType": "pagination",
            "url": "http://www.houseoffraser.co.uk/Cooling+Heating/S994,default,sc.html&cm_sp=Homepage-_-SaleMain-_-Link4&fix&spcl",
            "html": cache.load("http://www.houseoffraser.co.uk/Cooling+Heating/S994,default,sc.html&cm_sp=Homepage-_-SaleMain-_-Link4&fix&spcl")
        }

        it('should return 0/null items and null pages', function (done) {

            Structure(payload, function (err, pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": 1,
                    "url": "http://www.houseoffraser.co.uk/Cooling+Heating/S994,default,sc.html&cm_sp=Homepage-_-SaleMain-_-Link4&fix&spcl"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.houseoffraser.co.uk/LSA+Flower+rectangular+bunch+vase+22cm/161096227,default,sp.html?cgid=S5029&productna=161096227&referer=",
            "html": cache.load("http://www.houseoffraser.co.uk/LSA+Flower+rectangular+bunch+vase+22cm/161096227,default,sp.html?cgid=S5029&productna=161096227&referer=")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": null,
                    "price": null,
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "http://www.houseoffraser.co.uk/Electricals/S10,default,sc.html?cm_sp=Homepage-_-SaleMain-_-Link4",
            "html": cache.load("http://www.houseoffraser.co.uk/Electricals/S10,default,sc.html?cm_sp=Homepage-_-SaleMain-_-Link4")
        }

        it('should be true when number of items and pages correct', function () {

            var expected = JSON.stringify({
                "name": "Children's Puzzles",
                "url":"https://www.bitsandpieces.com/category/childrens-puzzles"
            })

            const category = Structure(payload)

            JSON.stringify(category).should.equal(expected);
        });

    });

    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.houseoffraser.co.uk/brand/hamleys/0.0.0.0033559.0.0.pl",
            "html": cache.load("https://www.houseoffraser.co.uk/brand/hamleys/0.0.0.0033559.0.0.pl")
        }

        // Change to be the number of products on the page
        var x = 7;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = 'Critters Go For A Ride 100 Large Piece Jigsaw Puzzle';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '4.99',
                url: 'http://www.bitsandpieces.com/product/critters-go-for-a-ride-100-large-piece-jigsaw-puzzle-42777',
                image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/42777.jpg',
                country: 'us'
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)

        })

        var lastProductName = 'Soccer Sliding Wooden Puzzle';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '14.99',
                url: 'http://www.bitsandpieces.com/product/soccer-sliding-wooden-puzzle',
                image: 'https://s3.amazonaws.com/cdn.bitsandpieces.com/images/160/43952.jpg',
                country: 'us'
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })

    }); 

});
