var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.housershoes.com/ad-tec-men-s-9-steel-toe-crazy-horse-logger-boot.html",
            "html": cache.load("http://www.housershoes.com/ad-tec-men-s-9-steel-toe-crazy-horse-logger-boot.html")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Ad Tec Men's 9\" Steel Toe Logger Boot",
                    "price": "109.88",
                    "url": payload.url,
                    "inStock": "UNCHECKED",
                    "stockLevel": null,
                    "image": "http://cdn.housershoes.com/media/catalog/product/cache/1/image/400x400/9df78eab33525d08d6e5fb8d27136e95/2/2/22523_1_1.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

        describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.housershoes.com/men.html",
            "html": cache.load("http://www.housershoes.com/men.html")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 100,
                    "pages": 4,
                    "url":"http://www.housershoes.com/men.html"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.housershoes.com/men.html",
            "html": cache.load("http://www.housershoes.com/men.html")
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Men",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.housershoes.com/women.html?dir=desc&featured_shoes=On+Sale&order=news_from_date&p=2",
            "html": cache.load("https://www.housershoes.com/women.html?dir=desc&featured_shoes=On+Sale&order=news_from_date&p=2")
        }

        // Change to be the number of products on the page
        var x = 32;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = "Asics Women's Contend 4 Silver Campanula Carbon";
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '59.88',
                    url: 'https://www.housershoes.com/asics-women-s-contend-4-silver-campanula-carbon-1.html',
                    image: 'https://www.housershoes.com/media/catalog/product/cache/1/small_image/135x/9df78eab33525d08d6e5fb8d27136e95/1/9/19755-1-base-small_1.jpg',
                    country: 'us',
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = "b.o.c Women's Alisha Black";
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '34.88',
                    url: 'https://www.housershoes.com/b-o-c-women-s-alisha-black-1.html',
                    image: 'https://www.housershoes.com/media/catalog/product/cache/1/small_image/135x/9df78eab33525d08d6e5fb8d27136e95/1/8/18606-1-base-small_2.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
