var should = require('should');
var Structure = require('../lib/structure.js')
let cache = require('../lib/cache')

describe('www.houzz.com', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.houzz.com/photos/24015931/Santi-Bar-Stool-Walnut-and-Cream-contemporary-bar-stools-and-counter-stools",
            "html": cache.load("http://www.houzz.com/photos/24015931/Santi-Bar-Stool-Walnut-and-Cream-contemporary-bar-stools-and-counter-stools")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Santi Bar Stool, Walnut and Cream",
                    "price": "100.06",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://st.hzcdn.com/simgs/35e1ed2704da91b2_4-9871/contemporary-bar-stools-and-counter-stools.jpg',
                    "country": 'us',
                    "description": "Sold As Set of 118.5\"L x 20\"W x 39-44.25\"HSeat height: 27.5 - 32.75\"The smooth supportive contours of the Santi barstool make this one of our most versatile pieces ever. The seat and back's slightly raised edges maximize comfort and add a contemporary edge. The cool seat sits atop a chrome base and modern curved footrest. Note: Due to the natural variation of wood, color may vary slightly."
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.houzz.com/photos/695852/Byron-Armchair-Currently-out-of-stock-contemporary-armchairs-and-accent-chairs-other",
            "html": cache.load("http://www.houzz.com/photos/695852/Byron-Armchair-Currently-out-of-stock-contemporary-armchairs-and-accent-chairs-other")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Byron Armchair - Currently out of stock.",
                    "price": "999",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'https://st.hzcdn.com/simgs/8ee1fe7e0f686c5a_4-0318/contemporary-armchairs-and-accent-chairs.jpg',
                    "country": 'us',
                    "description": 'The Byron Armchair features a modern, yet traditional look and feel. We love that it keeps those old-fashioned little touches, but updates it with curved detail stitching and glossy leather. Quick growing pine is used for the frame and mature rubberwood for the legs. Rubberwood is sourced from old plantation trees that are usually burnt when their supply of rubber is exhausted, but this way they can be reused - itâ€™s one of the most environmentally friendly woods available. The cover is made from 100% genuine high-grade leather. All Armchairs: http://www.fashionforhome.com/armchairs-lounge-chairs'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.houzz.com/photos/kitchen-and-dining-furniture",
            "html": cache.load("http://www.houzz.com/photos/kitchen-and-dining-furniture")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 36,
                    "pages": 1445,
                    "url":"http://www.houzz.com/photos/kitchen-and-dining-furniture"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.houzz.com/photos/kitchen-and-dining-furniture",
            "html": cache.load("http://www.houzz.com/photos/kitchen-and-dining-furniture")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Kitchen & Dining Furniture",
                    "url":"http://www.houzz.com/photos/kitchen-and-dining-furniture"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.houzz.co.uk/photos/home-office-accessories",
            "html": cache.load("https://www.houzz.co.uk/photos/home-office-accessories")
        }

        // Change to be the number of products on the page
        var x = 36;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Filton Quilted Counter Stool, Ivory';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '139.99',
                    url: 'http://www.houzz.com/photos/12588526/Filton-Quilted-Counter-Stool-Ivory-transitional-bar-stools-and-counter-stools',
                    image: 'https://st.hzcdn.com/fimgs/eb11223203866fbc_5023-w300-h300-b1-p10--transitional-bar-stools-and-counter-stools.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Casper Dining Side Chair, Clear';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '98.70',
                    url: 'http://www.houzz.com/photos/23129487/Casper-Dining-Side-Chair-Clear-dining-chairs',
                    image: 'https://st.hzcdn.com/fimgs/7001936a04c7ef16_8867-w300-h300-b1-p10--dining-chairs.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
