var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.hsn.com/products/fujifilm-instax-mini-instant-film-camera-wphoto-paper/7339675"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Fujifilm Instax Mini Instant Film Camera with 2-pack of Photo Film Paper",
                    "price": "99.95",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://i04.hsncdn.com/is/image/HomeShoppingNetwork/prodfull/fujifilm-instax-mini-instant-film-camera-wphoto-paper-d-2015101215170986~315261_503.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.hsn.com/shop/video-games/ec0220?view=all"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 60,
                    "pages": 9
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.hsn.com/shop/cameras/ec0407"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Cameras"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.hsn.com/shop/action-figures/ty0005"
        }

        // Change to be the number of products on the page
        var x = 30;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Nintendo Zelda Link Deluxe Articulated Big Figure';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '39.95',
                    url: 'https://www.hsn.com/products/nintendo-zelda-link-deluxe-articulated-big-figure/8422564',
                    image: 'https://i02.hsncdn.com/is/image/HomeShoppingNetwork/prodgrid230/nintendo-zelda-link-deluxe-articulated-big-figure-d-20170608170806997~555725.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Star Wars Black Series Finn 6" Action Figure';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '30.95',
                    url: 'https://www.hsn.com/products/star-wars-black-series-finn-6-action-figure/8248059',
                    image: 'https://i01.hsncdn.com/is/image/HomeShoppingNetwork/prodgrid230/star-wars-black-series-finn-6-action-figure-d-20170214114411233~1186842.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
