var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.iherb.com/Madre-Labs-Omega-3-Premium-Fish-Oil-100-Fish-Gelatin-Softgels/62118"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Madre Labs, Omega-3 Premium Fish Oil, 100 Fish Gelatin Softgels",
                    "price": "5.95",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://www.images-iherb.com/v/MLI-00952-3.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.iherb.com/Supplements"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 192,
                    "pages": 55
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.iherb.com/Supplements"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Supplements"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.iherb.com/c/145-intelligent-skincare-for-men"
        }

        // Change to be the number of products on the page
        var x = 6;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = '145 Intelligent Skincare for Men, After Shave Skin Soother, By Earth Science, 5.9 fl oz (174 ml)';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '8.40',
                    url: 'https://www.iherb.com/pr/145-Intelligent-Skincare-for-Men-After-Shave-Skin-Soother-By-Earth-Science-5-9-fl-oz-174-ml/59067',
                    image: 'https://www.images-iherb.com/k/ISM-00621-1.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = '145 Intelligent Skincare for Men, Just One Wash for Hair & Body, By Earth Science, 8 fl oz (237 ml)';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '9.56',
                    url: 'https://www.iherb.com/pr/145-Intelligent-Skincare-for-Men-Just-One-Wash-for-Hair-Body-By-Earth-Science-8-fl-oz-237-ml/59064',
                    image: 'https://www.images-iherb.com/k/ISM-00601-1.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
