var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.improvementscatalog.com/octopus-lantern/home-accents/all-home-accents/334328"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Octopus Lantern",
                    "price": "149.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://improvements.scene7.com/is/image/Improvements/T_WithoutZoom?$PDPmain$&$src=491592_main',
                    "country": 'us',
                    "description": 'Our Octopus Lantern is armed with handy ambient lighting.This aluminum octopus is very handy to have around when you need a little light for ambiance. Three of its arms hold glowing lanterns, while the other 5 keep it balanced on your shelf, desk, or table. This octopus lantern is sure to be a real conversation starter because of its unique design Each of the 3 lanterns include a glass cup that is protected by a hinged door Suitable for indoor or outdoor use Crafted of aluminum, iron, and glass A hand-painted bronze finish adds to its beauty Candle cups can use real tea lights candles or rechargeable tea lights'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    /*describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : ""
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });*/

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.improvementscatalog.com/home-accents/all-home-accents/#w=&af=cat3:homedecor_homeaccents_allhomeaccents cat2:homedecor_homeaccents cat1:homedecor pagetype:products"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 12,
                    "pages": 5
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.improvementscatalog.com/home-accents/all-home-accents/#w=&af=cat3:homedecor_homeaccents_allhomeaccents cat2:homedecor_homeaccents cat1:homedecor pagetype:products"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "All Home Accents"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.improvementscatalog.com/home-accents/all-home-accents/#w=&af=cat3:homedecor_homeaccents_allhomeaccents cat2:homedecor_homeaccents cat1:homedecor pagetype:products"
        }

        // Change to be the number of products on the page
        var x = 12;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Octopus Lantern';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '149.99',
                    url: 'http://www.improvementscatalog.com/octopus-lantern/home-accents/all-home-accents/334328',
                    image: 'http://improvements.resultspage.com/thumb.php?base=http://improvements.scene7.com/is/image/Improvements/T_WithoutZoom?$&sizeType=SLI_GridView&mfpart=491592&sale=&defAtt=main',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Glass & Metal Architectural Candle Lantern';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '29.99',
                    url: 'http://www.improvementscatalog.com/glass-26-metal-architectural-candle-lantern/home-accents/all-home-accents/322354',
                    image: 'http://improvements.resultspage.com/thumb.php?base=http://improvements.scene7.com/is/image/Improvements/T_WithoutZoom?$&sizeType=SLI_GridView&mfpart=483003&sale=&defAtt=main',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
