var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.interstatemusic.com/9981-Remo-BA011400-14-Coated-Ambassador-Batter-Drumhead-BA011400.aspx",
            "html": cache.load("http://www.interstatemusic.com/9981-Remo-BA011400-14-Coated-Ambassador-Batter-Drumhead-BA011400.aspx")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": 'Remo BA011400 14" Coated Ambassador Batter Drumhead',
                    "price": "15.28",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://az58332.vo.msecnd.net/e88dd2e9fff747f090c792316c22131c/Images/Products9981-371x350-1669802.jpg',
                    "country" : 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.interstatemusic.com/215155-Glee-The-Music-The-Christmas-Album-Piano-Vocal-Guitar-Songbook.aspx",
            "html": cache.load("http://www.interstatemusic.com/215155-Glee-The-Music-The-Christmas-Album-Piano-Vocal-Guitar-Songbook.aspx")
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Glee: The Music - The Christmas Album - Piano/Vocal/Guitar Songbook",
                    "price": "16.99",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://az58332.vo.msecnd.net/e88dd2e9fff747f090c792316c22131c/Images/Products215155-371x350-573266.jpg',
                    "country": 'us'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });


    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.interstatemusic.com/Categories/759-Drums-And-Percussion-Drum-and-Percussion-Accessories.aspx/1",
            "html": cache.load("http://www.interstatemusic.com/Categories/759-Drums-And-Percussion-Drum-and-Percussion-Accessories.aspx/1")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 48,
                    "pages": 83,
                    "url": payload.url
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.interstatemusic.com/Categories/759-Drums-And-Percussion-Drum-and-Percussion-Accessories.aspx/1",
            "html": cache.load("http://www.interstatemusic.com/Categories/759-Drums-And-Percussion-Drum-and-Percussion-Accessories.aspx/1")
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Drum and Percussion Accessories",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.interstatemusic.com/Categories/915-Drums-And-Percussion-Electronic-Drums.aspx/1",
            "html": cache.load("https://www.interstatemusic.com/Categories/915-Drums-And-Percussion-Electronic-Drums.aspx/1")
        }

        // Change to be the number of products on the page
        var x = 12;
        it('should return ' + x + ' number of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = "Roland TD1KV V-Drum Compact Electronic Drum Kit, Silent Kick, Mesh Snare Drum Head";
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: "76129450637",
                    name: firstProductName,
                    price: '599.99',
                    url: 'https://www.interstatemusic.com/963482-Roland-TD1KV-V-Drum-Compact-Electronic-Drum-Kit-Silent-Kick-Mesh-Snare-Drum-Head-TD1KV.aspx',
                    image: 'https://az58332.vo.msecnd.net/e88dd2e9fff747f090c792316c22131c/Images/Products963482-120x120-2757076.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Vic Firth PAD6 Soft Surface 6" Single Sided Drum Practice Pad';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: "694318018774",
                    name: lastProductName,
                    price: '20.28',
                    url: 'www.interstatemusic.com/35615-Vic-Firth-PAD6-Soft-Surface-6-Single-Sided-Drum-Practice-Pad-PAD6.aspx',
                    image: 'http://az58332.vo.msecnd.net/e88dd2e9fff747f090c792316c22131c/Images/Products35615-120x120-107175.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
