var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.jarrold.co.uk/departments/toys-and-hobbies/action-figures-and-accessories/mattel-minecraft-basic-action-figure"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Mattel Minecraft Basic Action Figure",
                    "price": "14.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'www.jarrold.co.uk/userdata/root/images/products/mattel/dnh08.jpg',
                    "country": 'uk'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.jarrold.co.uk/departments/toys-and-hobbies/action-figures-and-accessories"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Action Figures"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });
    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.jarrold.co.uk/departments/toys-and-hobbies/dolls-and-accessories"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 42,
                    "pages": 2
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Not a Search page', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.jarrold.co.uk/departments/toys-and-hobbies/action-figures-and-accessories/hasbro-transformers-robots-in-disguise-minicon"
        }

        it('should be true when items and pages are null', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.jarrold.co.uk/departments/toys-and-hobbies/action-figures-and-accessories"
        }

        // Change to be the number of products on the page
        var x = 42;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Mattel WWE Create a Ring';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '40.00',
                    url: 'http://www.jarrold.co.uk/departments/toys-and-hobbies/action-figures-and-accessories/mattel-wwe-create-a-ring',
                    image: 'http://www.jarrold.co.uk/userdata/root/images/products/mattel/cmb52.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Imaginext The Batcave';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '45.00',
                    url: 'http://www.jarrold.co.uk/departments/toys-and-hobbies/action-figures-and-accessories/imaginext-the-batcave',
                    image: 'http://www.jarrold.co.uk/userdata/root/images/products/batcave.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
