var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.jefferspet.com/products/blue-bits-natural-soft-moist-training-treats?via=5338847b9fa2600f00000029%2F5338847b9fa2600f0000002a%2F55525fd8fba3fd24e100008f"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Blue Bits Training Treats",
                    "price": "5.79",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://jefferspet-weblinc.netdna-ssl.com/product_images/blue-bits-natural-soft-moist-training-treats/Chicken/56463574fba3fd6234000087/detail.jpg?c=1459435087',
                    "country": 'us',
                    "description": 'Natural dog training treats that are soft, moist and extremely tasty.'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null,
    //                 "image": '',
    //                 "country": '',
    //                 "description": ''
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.jefferspet.com/categories/dog/food-treats/all-dog-treats"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 32,
                    "pages": 10
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.jefferspet.com/categories/dog/food-treats/blue-buffalo"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Dog Blue Buffalo"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.jefferspet.com/categories/dog/food-treats/blue-buffalo"
        }

        // Change to be the number of products on the page
        var x = 15;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Blue Bits Training Treats';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '5.79',
                    url: 'https://www.jefferspet.com/products/blue-bits-natural-soft-moist-training-treats?via=5338847b9fa2600f00000029%2F5338847b9fa2600f0000002a%2F55525fd8fba3fd24e100008f',
                    image: 'https://jefferspet-weblinc.netdna-ssl.com/product_images/blue-bits-natural-soft-moist-training-treats/Chicken/56463574fba3fd6234000087/large_thumb.jpg?c=1459435087',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = "Blue Buffalo Blue's Stew Canned Dog Food";
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '2.89',
                    url: 'https://www.jefferspet.com/products/blue-buffalo-canned-dog-food-12-oz?via=5338847b9fa2600f00000029%2F5338847b9fa2600f0000002a%2F55525fd8fba3fd24e100008f',
                    image: 'https://jefferspet-weblinc.netdna-ssl.com/product_images/blue-buffalo-hearty-beef-stew-12-oz/554291e8fba3fd756500001d/large_thumb.jpg?c=1442842557',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
