var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.jomashop.com/casio-gshock-military-gd100-1b.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Casio G-Shock Military Men's Watch",
                    "price": "59.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://cdn2.jomashop.com/media/catalog/product/cache/1/image/490x490/9df78eab33525d08d6e5fb8d27136e95/c/a/casio-gshock-military-mens-watch-gd1001b.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.jomashop.com/casio-watches.html?gender=25#104691"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Casio Watches"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.jomashop.com/casio-watches.html?gender=25#104691"
        }

        // Change to be the number of products on the page
        var x = 55;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = "Casio G-Shock Military Men's Watch";
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '59.99',
                    url: 'http://www.jomashop.com/casio-gshock-military-gd100-1b.html',
                    image: 'http://cdn2.jomashop.com/media/catalog/product/cache/1/small_image/360x/9df78eab33525d08d6e5fb8d27136e95/c/a/casio-gshock-military-mens-watch-gd1001b.jpg',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = "Casio G Shock - Big Case Analog Digital \"Ice Gray\" - Men's Watch GA100LG-8A";
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '85.00',
                    url: 'http://www.jomashop.com/casio-watch-ga1001g-8acr.html',
                    image: 'http://cdn2.jomashop.com/media/catalog/product/cache/1/small_image/360x/9df78eab33525d08d6e5fb8d27136e95/c/a/casio-g-shock-big-case-analog-digital-ice-gray-mens-watch-ga100lg8a-ga100lg8acr.jpg',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
