describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.jpcycles.com/harley-dyna-parts",
        "html": cache.load("https://www.jpcycles.com/harley-dyna-parts")
    }

    // Change to be the number of products on the page
    var x = 24;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Vance & Hines Fuelpak FP3 Fuel Management System';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '399.99',
                url: 'https://www.jpcycles.com/product/126-995/vance-hines-fuelpak-fp3-fuel-management-system',
                image: 'https://img.jpcycles.com/product/126-995_A.jpg',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Dunlop American Elite MT90B16 Rear Tire';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '159.00',
                url: 'https://www.jpcycles.com/product/105-370/dunlop-american-elite-mt90b16-rear-tire',
                image: 'https://img.jpcycles.com/product/105-370_A.jpg',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.jpcycles.com/harley-dyna-parts",
            "html": cache.load("https://www.jpcycles.com/harley-dyna-parts")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Parts & Accessories for Harley-Davidson Dyna",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
