var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithDomainName', function () {

    this.timeout(5000);

    describe('In Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "https://www.justmylook.com/andis-us-fade-adjustable-blade-clipper-p9029",
            "html": cache.load("https://www.justmylook.com/andis-us-fade-adjustable-blade-clipper-p9029")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Andis US Fade Adjustable Blade Clipper",
                    "price": "47.99",
                    "url": payload.url,
                    "inStock": 'UNCHECKED',
                    "stockLevel": null,
                    "image": "https://www.justmylook.com/images/andis-us-fade-adjustable-blade-clipper-p9029-11175_medium.jpg",
                    "country": 'uk',
                    "description": "Andis US Fade Adjustable Blade Clipper is built on an extremely powerful (yet quiet) magnetic motor that offers an impressive 6,000 cutting strokes per minute - making it ideal for any hair type! The Fade clipper offers a high-speed adjustable blade which can be zero gapped making it ideal for tapering and fading.Key Features:Cutting Length: 0.2-0.5mmThumb lever to adjust the taper without changing blades.Perfect fo fading and tapering.Kit Includes:1 x Clipper6 x Attachment combs (#1-#4 + #6 + #8)1 x Blade guard1 x Blade brush1 x Blade oil."
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "",
            "html": cache.load("")
        }

        xit('should be false when product is in store only', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.justmylook.com/electricals-c33/clippers-trimmers-c107",
            "html": cache.load("https://www.justmylook.com/electricals-c33/clippers-trimmers-c107")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Clippers & Trimmers",
                    "url": 'https://www.justmylook.com/electricals-c33/clippers-trimmers-c107'
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function () {

                var payload = {
                    "pageType": "pagination",
                    "url": "https://www.justmylook.com/electricals-c33/clippers-trimmers-c107",
                    "html": cache.load("https://www.justmylook.com/electricals-c33/clippers-trimmers-c107")
                }

                it('should be true when number of items and pages correct', function (done) {

                    Structure(payload, function (err, pagination) {
                        var expected = JSON.stringify({
                            "items": 42,
                            "pages": 1,
                            "url": 'https://www.justmylook.com/electricals-c33/clippers-trimmers-c107'
                        })
                        JSON.stringify(pagination).should.equal(expected);
                        done()
                    })
                });

            });


    describe('Search Page', function () {

        var payload = {
            "pageType": "search",
            "url": "https://www.justmylook.com/electricals-c33/clippers-trimmers-c107",
            "html": cache.load("https://www.justmylook.com/electricals-c33/clippers-trimmers-c107")
        }

        // Change to be the number of products on the page
        var x = 42;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function (err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Wahl Cordless Magic Clip Clipper';
        it('first product should be ' + firstProductName, function (done) {
            Structure(payload, function (err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '100.49',
                    url: 'https://www.justmylook.com/wahl-cordless-magic-clip-clipper-p7795',
                    image: 'https://www.justmylook.com/images/wahl-cordless-magic-clip-clipper-p7795-8994_thumb.jpg',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Andis US Fade Adjustable Blade Clipper';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function (err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '47.99',
                    url: 'https://www.justmylook.com/andis-us-fade-adjustable-blade-clipper-p9029',
                    image: 'https://www.justmylook.com/images/andis-us-fade-adjustable-blade-clipper-p9029-11175_thumb.jpg',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});


