describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.jwpepper.com/sheet-music/Piano_Catalog_Popular_Pop_Rock_Classic_Rock.list",
        "html": cache.load("https://www.jwpepper.com/sheet-music/Piano_Catalog_Popular_Pop_Rock_Classic_Rock.list")
    }

    // Change to be the number of products on the page
    var x = 48;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'The Big Black Piano Songbook';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: null,
                url: 'https://www.jwpepper.com/The-Big-Black-Piano-Songbook/10909152.item',
                image: 'https://d2fizz4npx5v6x.cloudfront.net/covers/1/10909152.jpg',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Best of the Eagles-Easy Piano';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: null,
                url: 'https://www.jwpepper.com/Best-of-the-Eagles-Easy-Piano/5428719.item',
                image: 'https://d2fizz4npx5v6x.cloudfront.net/covers/5/5428719.gif',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.jwpepper.com/sheet-music/Piano_Catalog_Popular_Pop_Rock_Classic_Rock.list",
            "html": cache.load("https://www.jwpepper.com/sheet-music/Piano_Catalog_Popular_Pop_Rock_Classic_Rock.list")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Classic Rock Favorites",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
