var should = require('should');
var Structure = require('../lib/structure.js')

describe('kateSpadecom', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.katespade.com/products/reservoir-road-carolyn/098687005548.html"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "reservoir road carolyn",
                    "price": "198.00",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'https://katespade.insnw.net/KateSpade/PXRU7443_001?$large$',
                    "country": "",
                    "description": 'a sleek zip-top bag with a matching shoulder strap and a pair of contrasting tassels, the reservoir road carolyn is amazingly versatile, as easy to wear with jeans and a sweater as with a cocktail dress.'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });
    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.katespade.com/on/demandware.store/Sites-Shop-Site/en_US/Search-Show?q=&sz=99"
        }

        // Change to be the number of products on the page
        var x = 99;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'reservoir road carolyn';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '198.00',
                    url: 'http://www.katespade.com/products/reservoir-road-carolyn/098687005548.html',
                    image: 'https://katespade.insnw.net/KateSpade/PXRU7443_001_1?$s7productgrid$',
                    country: ''
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'bridal planner';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '48.00',
                    url: 'http://www.katespade.com/products/bridal-planner/167830.html',
                    image: 'https://katespade.insnw.net/KateSpade/167830_425_1?$s7productgrid$',
                    country: ''
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
