    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.kennethcole.com/men/shoes/"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Men's Shoes"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.kennethcole.com/men/shoes/"
        }

        // Change to be the number of products on the page
        var x = 216;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'World Champion Loafer';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '85.00',
                    url: 'http://www.kennethcole.com/men/shoes/world-champion-loafer-MR00931SY.html?dwvar_MR00931SY_color=001&dwvar_MR00931SY_size=50395',
                    image: 'http://demandware.edgesuite.net/sits_pod27/dw/image/v2/AAVO_PRD/on/demandware.static/-/Sites-kennethcole-master-catalog/default/dw9e3084f2/images/MR00931SY_001_MAIN.jpg?sw=373&sh=498&sm=cut',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'De-Luxe Leather and Neoprene Boot';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '395.00',
                    url: 'http://www.kennethcole.com/men/shoes/de-luxe-leather-and-neoprene-boot-QMF6YL001.html?dwvar_QMF6YL001_size=50635&dwvar_QMF6YL001_color=001',
                    image: 'http://demandware.edgesuite.net/sits_pod27/dw/image/v2/AAVO_PRD/on/demandware.static/-/Sites-kennethcole-master-catalog/default/dwa5fba5de/images/QMF6YL001_001_MAIN.jpg?sw=373&sh=498&sm=cut',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

