var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.keurig.com/Beverages/Coffee/Breakfast-Blend-Coffee/p/Breakfast-Blend-Coffee-K-Cup-Green-Mountain"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Breakfast Blend Coffee",
                    "price": "14.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://s7d4.scene7.com/is/image/keurig/Breakfast-Blend-Coffee-K-Cup-Green-Mountain_en_pdp?$pdp_general$',
                    "country": 'us',
                    "description": 'Now available in a recyclable K-Cup® pod (24 Count only), Breakfast Blend is our classic New England breakfast cup. Breakfast Blend offers a crisp Central American coffee matched with the depth of an Indonesian bean to create a bright, wakeful blend. To learn about our recycling efforts and how we’re striving to make every cup matter click here.'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.keurig.com/Beverages/Coffee/Smooth-Coffee/p/smooth-coffee-krispy-kreme-k-cup"
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Smooth Coffee",
                    "price": null,
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": 'http://s7d4.scene7.com/is/image/keurig/smooth-coffee-krispy-kreme-k-cup_en_pdp?$pdp_general$',
                    "country": 'us',
                    "description": 'A balanced, easy-to-love blend that boasts bright fruit notes and a clean, sweet finish.'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.keurig.com/search?text=coffee"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 21,
                    "pages": 14
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.keurig.com/carafes-%26-drinkware101/c/carafes-and-drinkware101"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Stainless Steel Tumblers & Travel Mugs"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.keurig.com/search?text=coffee"
        }

        // Change to be the number of products on the page
        var x = 21;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Breakfast Blend Coffee';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '14.99',
                    url: 'http://www.keurig.com/Beverages/Coffee/Breakfast-Blend-Coffee/p/Breakfast-Blend-Coffee-K-Cup-Green-Mountain',
                    image: 'http://s7d4.scene7.com/is/image/keurig/Breakfast-Blend-Coffee-K-Cup-Green-Mountain_en_general?wid=272&hei=202&fmt=png-alpha&qlt=75,1&op_sharpen=0&resMode=bicub&op_usm=1,1,6,0&iccEmbed=0&printRes=72&extend=0,250,0,0',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Big Easy Bold™ Coffee';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '14.99',
                    url: 'http://www.keurig.com/Beverages/Coffee/Big-Easy-Bold%E2%84%A2-Coffee/p/Big-Easy-Bold-Coffee-K-Cup-Emerils',
                    image: 'http://s7d4.scene7.com/is/image/keurig/Big-Easy-Bold-Coffee-K-Cup-Emerils_en_general?wid=272&hei=202&fmt=png-alpha&qlt=75,1&op_sharpen=0&resMode=bicub&op_usm=1,1,6,0&iccEmbed=0&printRes=72&extend=0,250,0,0',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
