var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')


describe('www.kiddicare.com', function() {

    this.timeout(5000);

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.kiddicare.com/category/clearance",
            "html" : cache.load("https://www.kiddicare.com/category/clearance")
        }

        it('should return 46 products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(121);
                done()
            })
        });

        it('last product should be My 1st JCB Big Wheeler Doug', function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'My 1st JCB Big Wheeler Doug',
                    price: '9.99',
                    url: 'https://www.kiddicare.com/product/my-1st-jcb-big-wheeler-doug-443787?product-id=848418',
                    image: 'https://images.static.worldstores.co/images/stickers/loz_reduced-to-clear.png',
                    country: 'uk'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        it('first product should be SwaddleMe Original Swaddle Grey', function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: "SwaddleMe Original Swaddle Grey",
                    price: '7.99',
                    url: 'https://www.kiddicare.com/product/swaddleme-original-swaddle-grey-442256?product-id=845732',
                    image: 'https://images.static.worldstores.co/images/stickers/loz_reduced-to-clear.png',
                    country: 'uk'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

    });


    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.kiddicare.com/p/Tiny_Love_Tiny_Smarts_Spin_Ball.htm",
            "html" : cache.load("http://www.kiddicare.com/p/Tiny_Love_Tiny_Smarts_Spin_Ball.htm")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Tiny Love Tiny Smarts Spin Ball",
                    "price": "6.39",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://images.static.worldstores.co/images/products/WP/D3/Tiny_Love_Tiny_Smarts_Spin_Ball_1.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });


    describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.kiddicare.com/p/Fisher-Price_Lil_Snoopy.htm",
            "html" : cache.load("http://www.kiddicare.com/p/Fisher-Price_Lil_Snoopy.htm")
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Fisher-Price Lil Snoopy",
                    "price": "8.00",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "https://images.static.worldstores.co/images/products/WP/35/Fisher_Price_Lil_Snoopy_A_SS_1.jpg",
                    "country": "uk"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.kiddicare.com/c/Buggies_and_Strollers.htm",
            "html" : cache.load("http://www.kiddicare.com/c/Buggies_and_Strollers.htm")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 60,
                    "pages": 3,
                    "url":"http://www.kiddicare.com/c/Buggies_and_Strollers.htm"
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.kiddicare.com/c/Interactive_and_Electronic_Toys.htm",
            "html" : cache.load("http://www.kiddicare.com/c/Interactive_and_Electronic_Toys.htm")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Interactive & Electronic Toys",
                    "url":"http://www.kiddicare.com/c/Interactive_and_Electronic_Toys.htm"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


});
