var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.kidsfootlocker.com/product/model:263854/sku:26673004/nike-kyrie-2-boys-grade-school/kyrie-irving/grey/blue/"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Nike Kyrie 2 - Boys' Grade School",
                    "price": "94.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://images.footlocker.com/is/image/EBFL2/26673004_fr_ven_sc7?fit=constrain,1&wid=500&hei=500&fmt=jpg',
                    "country": 'us',
                    "description": 'The Nike Kyrie 2 is fast. Really fast. It\'s built for those game changing ball-handlers, the ones who lead the fast breaks and take control of the offense. Midfoot strap provides the ultimate lockdown. Foam midsole provides lightweight cushioning. Deep-cut forefoot flex grooves for flexible, natural motion. Mesh and textile upper is durable and supportive.'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    /*describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : ""
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": '',
                    "description": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });*/

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.kidsfootlocker.com/Boys/Shoes/_-_/N-1cuZrj?Rpp=180&cm_PAGE=180&Nr=AND%28P_RecordType%3AProduct%29"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 180,
                    "pages": 18
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.kidsfootlocker.com/Boys/Shoes/_-_/N-1cuZrj?Rpp=180&cm_PAGE=180&Nr=AND%28P_RecordType%3AProduct%29"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Boys Shoes"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.kidsfootlocker.com/Boys/Shoes/_-_/N-1cuZrj?Rpp=180&cm_PAGE=180&Nr=AND%28P_RecordType%3AProduct%29"
        }

        // Change to be the number of products on the page
        var x = 180;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Nike Kyrie 2 - Boys\' Grade School';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '94.99',
                    url: 'http://www.kidsfootlocker.com/product/model:263854/sku:26673004/nike-kyrie-2-boys-grade-school/kyrie-irving/grey/blue/',
                    image: 'http://images.footlocker.com/pi/26673004/large/nike-kyrie-2-boys-grade-school',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Nike KD 9 - Boys\' Toddler';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '59.99',
                    url: 'http://www.kidsfootlocker.com/product/model:265359/sku:5910100/nike-kd-9-boys-toddler/kevin-durant/white/black/',
                    image: 'http://images.footlocker.com/pi/5910100/large/nike-kd-9-boys-toddler',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
