var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.kirimhobby.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.kirinhobby.com/shop/product_info.php?products_id=12152",
            "html": cache.load("http://www.kirinhobby.com/shop/product_info.php?products_id=12152")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Doctor Who: Dalek (Blue Ver.) Clip On Talking 4\" [Plush] by Underground Toys",
                    "price": "10.95",
                    "url": payload.url,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://www.kirinhobby.com/shop/images/TY15/UT0012152_01.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    describe('Out of Stock Page', function () {

        var payload = {
            "pageType": "product",
            "url": "http://www.kirinhobby.com/shop/product_info.php?products_id=2207",
            "html": cache.load("http://www.kirinhobby.com/shop/product_info.php?products_id=2207")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function (err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Robotech: Masterpiece Beta Fighter vol. 4 [Action Figure] by Toynami",
                    "price": "144.95",
                    "url": payload.url,
                    "inStock": false,
                    "stockLevel": null,
                    "image": "http://www.kirinhobby.com/shop/images/TY1/TYTN02207_kh.jpg",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "http://www.kirinhobby.com/shop/products_new.php?cPath=320&page=2",
        "html": cache.load("http://www.kirinhobby.com/shop/products_new.php?cPath=320&page=2")
    }

    // Change to be the number of products on the page
    var x = 15;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'MG 1/100 Mobile Suit Gundam 00V: Battlefield Record ~ Gundam Avalanche [Model Kit] by Bandai';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '73.92',
                url: 'http://www.kirinhobby.com/shop/product_info.php?products_id=20998',
                image: 'http://www.kirinhobby.com/shop/images/TY24/DC0020800_khs.jpg',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Teen Titans: Wonder Girl Multi Part [Statue] by DC Collectibles';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '78.85',
                url: 'http://www.kirinhobby.com/shop/product_info.php?products_id=20800',
                image: 'http://www.kirinhobby.com/shop/images/TY24/BA0020998_khs.jpg',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "http://www.kirinhobby.com/shop/index.php?cPath=320",
            "html": cache.load("http://www.kirinhobby.com/shop/index.php?cPath=320")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Underground Toys",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
