var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

    describe('www.kmart.com', function() {
         this.timeout(5000);


    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.kmart.com/toys-games-action-figures-accessories/b-20108?subCatView=true&pageNum=10",
            "html" : cache.load("https://www.tesco.com/direct/microsoft-xbox-one-x-1tb-4k-console/234-7189.prd?skuId=234-7189")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, pagination) {
                var expected = JSON.stringify({
                    "items": 50,
                    "pages": 20
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.kmart.com/toys-games-action-figures-accessories/b-20108?subCatView=true&pageNum=10",
            "html" : cache.load("https://www.tesco.com/direct/microsoft-xbox-one-x-1tb-4k-console/234-7189.prd?skuId=234-7189")
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(err, category) {
                var expected = JSON.stringify({
                    "name": "Action Figures & Accessories"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.kmart.com/tvs-electronics-computers-laptops-tablets-ereaders-tablets/b-1231504207",
            "html" : cache.load("http://www.kmart.com/tvs-electronics-computers-laptops-tablets-ereaders-tablets/b-1231504207")
        }

        it('should return 50 products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(50);
                done()
            })
        });

        it('last product should be:', function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'KidTrax Green Moto Trax Toddler Quad',
                    price: '69.99',
                    url: 'http://www.kmart.com/kidtrax-green-moto-trax-toddler-quad/p-05247052000P?plpSellerId=Kmart&prdNo=50&blockNo=50&blockType=G50',
                    image: 'http://c.shld.net/rpx/i/s/i/spin/image/spin_prod_1070528912?hei=245&wid=245&op_sharpen=1&qlt=85',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        it('first product should be:', function(done) {

            Structure(payload, function(err, result) {

                 var first = result.shift()
                    var expected = JSON.stringify({
                        barcode: null,
                        name: 'Power Wheels Lil Lightning McQueen',
                        price: '119.00',
                        url: 'http://www.kmart.com/power-wheels-lil-lightning-mcqueen/p-004W007169066001P?plpSellerId=Kmart&prdNo=1&blockNo=1&blockType=G1',
                        image: 'http://c.shld.net/rpx/i/s/i/spin/10091566/prod_12207792712?hei=245&wid=245&op_sharpen=1&qlt=85',
                        country: 'us'
                        
                    })

                    JSON.stringify(first).should.equal(expected)
                    done()
                })

        })

    });


    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.kmart.com/family-feud-electronic-game/p-004W002987600001P?plpSellerId=Kmart&prdNo=42&blockNo=42&blockType=G42",
            "html" : cache.load("https://www.tesco.com/direct/microsoft-xbox-one-x-1tb-4k-console/234-7189.prd?skuId=234-7189")
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Family Feud Electronic Game",
                    "price": "19.99",
                    "url": "http://www.kmart.com/family-feud-electronic-game/p-004W002987600001P?plpSellerId=Kmart&prdNo=42&blockNo=42&blockType=G42",
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://c.shld.net/rpx/i/s/i/spin/10098671/prod_2191702612?hei=468&wid=468&op_sharpen=1',
                    "country": 'us'
                    
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });


    describe('In Store Only Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.kmart.com/proctor-silex-2-speed-blender/p-011W007418363001P?plpSellerId=Kmart&prdNo=16&blockNo=16&blockType=G16",
            "html" : cache.load("https://www.tesco.com/direct/microsoft-xbox-one-x-1tb-4k-console/234-7189.prd?skuId=234-7189")
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(err, inStockPage) {
                var expected = JSON.stringify({
                    "name": "Proctor Silex 2 Speed Blender",
                    "price": "15.00",
                    "url": "http://www.kmart.com/proctor-silex-2-speed-blender/p-011W007418363001P?plpSellerId=Kmart&prdNo=16&blockNo=16&blockType=G16",
                    "inStock": false,
                    "stockLevel": null,
                    "image": "http://c.shld.net/rpx/i/s/i/spin/image/spin_prod_1174634712?hei=624&wid=624&op_sharpen=1",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    });

});

