describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.knitpicks.com/Kits/View_All_Pattern_Kits__L300822.html",
        "html": cache.load("https://www.knitpicks.com/Kits/View_All_Pattern_Kits__L300822.html")
    }

    // Change to be the number of products on the page
    var x = 24;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    var firstProductName = 'Beginner Blanket Pattern Kit Crochet';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '79.84',
                url: 'https://www.knitpicks.com/kits/Beginner_Blanket_Pattern_Kit_Crochet__PKbeginnerblanketcroch.html',
                image: 'https://d2q9kw5vp0we94.cloudfront.net/groups/beginnerblanketcrochs.jpg',
                country: 'us'
            })
            JSON.stringify(first).should.equal(expected)
            done()
        })

    })

    var lastProductName = 'Helianthus Shawl';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '37.37',
                url: 'https://www.knitpicks.com/kits/Helianthus_Shawl__PKhelianthusshawl.html',
                image: 'https://d2q9kw5vp0we94.cloudfront.net/groups/helianthusshawls.jpg',
                country: 'us'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.knitpicks.com/Kits/View_All_Pattern_Kits__L300822.html",
            "html": cache.load("https://www.knitpicks.com/Kits/View_All_Pattern_Kits__L300822.html")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Pattern Kits",
                    "url": payload.url 
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});
