var should = require('should');
var Structure = require('../lib/structure.js')

describe('www.kohls.com', function() {

    before(function()  {
        this.timeout(6000);
    })

     describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "http://www.kohls.com/catalog/video-games-consoles-electronics.jsp?CN=4294713538+4294719631&icid=ega|a7"
        }

        it('should return 60 products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(60);
                done()
            })
        });

        it('last product should be Plants vs. Zombies Garden Warfare for Xbox One', function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: 'Plants vs. Zombies Garden Warfare for Xbox One',
                    price: '24.99',
                    url: 'http://www.kohls.com/product/prd-1947267/plants-vs-zombies-garden-warfare-for-xbox-one.jsp',
                    image: 'http://media.kohlsimg.com/is/image/kohls/1947267?wid=240&hei=240&op_sharpen=1',
                    country: 'us',
                    sku: '1947267'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

        it('first product should be a Xbox 500GB', function(done) {

            Structure(payload, function(result) {

                 var first = result.shift()
                    var expected = JSON.stringify({
                        barcode: null,
                        name: 'Xbox One 500GB Pick Your Game Bundle with Chatpad',
                        price: '299.99',
                        url: 'http://www.kohls.com/product/prd-2676324/xbox-one-500gb-name-your-game-2-bundle.jsp',
                        image: 'http://media1.kohlsimg.com/is/image/kohls/2676324?wid=240&hei=240&op_sharpen=1',
                        country: 'us',
                        sku: '2676324'
                    })

                    JSON.stringify(first).should.equal(expected)
                    done()
                })

        })


    });

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.kohls.com/catalog/video-games-consoles-electronics.jsp?CN=Category:Video%20Games%20%26%20Consoles+Department:Electronics&icid=ega|a7&PPP=60&WS=120"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 60,
                    "pages": 4
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "http://www.kohls.com/catalog/video-games-consoles-electronics.jsp?CN=4294713538+4294719631&icid=ega|a7"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Video Games & Consoles"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.kohls.com/product/prd-1141815/linon-allure-counter-stool.jsp?pfm=rrrecs-pmp-hor"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Linon Allure Counter Stool",
                    "price": "64.99",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "http://media.kohlsimg.com/is/image/kohls/1141815_Brown?wid=500&hei=500&op_sharpen=1",
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });


    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should return false when product is out of stock', function(done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": null,
    //                 "price": null,
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     })

    // })

  describe('Out of Stock Page 2', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.kohls.com/product/prd-1821387/hautman-brothers-collection-garden-gate-bluebirds-1000-pc-puzzle.jsp"
        }

        it('should return false when product is out of stock', function(done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": null,
                    "price": null,
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "country": "us"
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        })

    })

  describe('Checking to see if no pagination works', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "http://www.kohls.com/"
        }

        it('should return 0 items and null pages', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": null,
                    "pages": null
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

 });







