var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "http://www.koleimports.com/friction-big-wheel-super-power-pickup-truck",
            "html" : cache.load("http://www.koleimports.com/friction-big-wheel-super-power-pickup-truck"),

        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(error,inStockPage) {
                var expected = JSON.stringify({
                    "name": "Friction Big Wheel Super Power Pickup Truck",
                    "price": "3.77",
                    "url": "http://www.koleimports.com/friction-big-wheel-super-power-pickup-truck",
                    "inStock": true,
                    "stockLevel": null,
                    "image": 'http://drk77c84zgsqo.cloudfront.net/media/catalog/product/cache/1/image/360x360/9df78eab33525d08d6e5fb8d27136e95/O/C/OC754.jpg',
                    "country": 'us',
                    "barcode": '731015197613'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    // describe('Out of Stock Page', function() {

    //     var payload = {
    //         "pageType" : "product",
    //         "url" : ""
    //     }

    //     it('should be false when product is in store only', function (done) {

    //         Structure(payload, function(inStockPage) {
    //             var expected = JSON.stringify({
    //                 "name": "",
    //                 "price": "",
    //                 "url": null,
    //                 "inStock": false,
    //                 "stockLevel": null
    //             })
    //             JSON.stringify(inStockPage).should.equal(expected);
    //             done()
    //         })
    //     });

    // });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.koleimports.com/barbeque",
            "html" : cache.load("https://www.koleimports.com/barbeque"),
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(err,category) {
                var expected = JSON.stringify({
                    "name": "Wholesale Barbecue Products",
                    "url":"https://www.koleimports.com/barbeque"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.koleimports.com/barbeque",
            "html" : cache.load("https://www.koleimports.com/barbeque"),
        }

        // Change to be the number of products on the page
        var x = 16;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(err, result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Long Bamboo Skewers';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(err, result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '0.80',
                    url: 'https://www.koleimports.com/long-bamboo-skewers',
                    image: 'https://drk77c84zgsqo.cloudfront.net/media/catalog/product/cache/0/thumbnail/200x200/9df78eab33525d08d6e5fb8d27136e95/H/B/HB207_2.jpg',
                    country: 'us',
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'Deluxe Barbecue Grill Brush with Scraper';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(err, result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '3.15',
                    url: 'https://www.koleimports.com/deluxe-barbecue-grill-brush-with-scraper',
                    image: 'https://drk77c84zgsqo.cloudfront.net/media/catalog/product/cache/0/thumbnail/200x200/9df78eab33525d08d6e5fb8d27136e95/O/C/OC535.jpg',
                    country: 'us',
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
