var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.ledhut.com', function() {    

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.ledhut.co.uk/spot-lights/gu10-led-bulbs/5w-led-gu10-spotlight-daylight-4000k.html",
            "html": cache.load("https://www.ledhut.co.uk/spot-lights/gu10-led-bulbs/5w-led-gu10-spotlight-daylight-4000k.html")
        }

        it('should be true when product is in stock', function () {
            
           const inStockPage =  Structure(payload)       
           console.log(inStockPage)     
           inStockPage.name.should.be.equal("5W GU10 LED Glass Spotlight - 50W Replacement - 4000K - Non Dimmable");
           inStockPage.price.should.be.equal("2.26");
           inStockPage.url.should.be.equal("https://www.ledhut.co.uk/spot-lights/gu10-led-bulbs/5w-led-gu10-spotlight-daylight-4000k.html");
           inStockPage.inStock.should.be.equal(true);
           should(inStockPage.stockLevel).be.equal(null);
           inStockPage.inStock.should.be.equal(true);
           inStockPage.image.should.be.equal('https://cdn2.ledhut.co.uk/media/catalog/product/cache/1/image/700x700/9df78eab33525d08d6e5fb8d27136e95/g/l/glass_dl__1.jpg');
           inStockPage.country.should.be.equal('uk');            
        });

    });


    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.bitsandpieces.com/category/childrens-puzzles",
            "html": cache.load("https://www.bitsandpieces.com/category/childrens-puzzles")
        }

        it('should be true when number of items and pages correct', function () {

            var expected = JSON.stringify({
                "items": 7,
                "pages": null,
                "url":"https://www.bitsandpieces.com/category/childrens-puzzles"
            })

            const pagination = Structure(payload)

            JSON.stringify(pagination).should.equal(expected);
        });

    });

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.bitsandpieces.com/category/childrens-puzzles",
            "html": cache.load("https://www.bitsandpieces.com/category/childrens-puzzles")
        }

        it('should be true when number of items and pages correct', function () {

            var expected = JSON.stringify({
                "name": "Children's Puzzles",
                "url":"https://www.bitsandpieces.com/category/childrens-puzzles"
            })

            const category = Structure(payload)

            JSON.stringify(category).should.equal(expected);
        });

    });

    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.ledhut.co.uk/spot-lights/gu10-led-bulbs.html",
            "html": cache.load("https://www.ledhut.co.uk/spot-lights/gu10-led-bulbs.html")
        }

        // Change to be the number of products on the page
        var x = 13;
        it('should return ' + x + ' numer of products', function () {
            let result = Structure(payload)
            result.length.should.equal(x);
        });

        var firstProductName = '4W GU10 LED Spotlight - 35W Replacement - 2700K - Dimmable';
        it('first product should be ' + firstProductName, function() {

            var expected = JSON.stringify({
                barcode: null,
                name: firstProductName,
                price: '2.89',
                url: 'https://www.ledhut.co.uk/spot-lights/gu10-led-bulbs/4watt-gu10-led-spotlight-35w-replacement-2700k-dimmable.html',
                image: 'https://cdn2.ledhut.co.uk/media/catalog/product/cache/1/small_image/192x192/9df78eab33525d08d6e5fb8d27136e95/g/u/gu10_ww__1_4.jpg',
                country: 'uk'
            })
            
            let result = Structure(payload)

            var first = result.shift()
            
            JSON.stringify(first).should.equal(expected)

        })

        var lastProductName = 'GU10 Fly Lead - With Quick Connector';
        it('last product should be ' + lastProductName, function () {
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '2.69',
                url: 'https://www.ledhut.co.uk/spot-lights/gu10-led-bulbs/gu10-fly-lead-with-quick-connector.html',
                image: 'https://cdn2.ledhut.co.uk/media/catalog/product/cache/1/small_image/192x192/9df78eab33525d08d6e5fb8d27136e95/b/r/brushed.jpg',
                country: 'uk'
            })

            const result = Structure(payload);

            var last = result.pop()
            JSON.stringify(last).should.equal(expected)
        })

    }); 

});
    