var should = require('should');
var Structure = require('../lib/structure.js')
const cache = require('../lib/cache')

describe('www.lemonskincare.com', function () {

    this.timeout(10000);


    describe('In stock Page', function () {



        var payload = {
            "pageType": "product",
            "url": "https://www.lemonskincare.com/collections/professional-products/products/dynamic-skin-recovery-spf50",
            "html": cache.load("https://www.lemonskincare.com/collections/professional-products/products/dynamic-skin-recovery-spf50")
        }

        it('should', function (done) {

            Structure(payload, function (err, result) {

                should(err).be.equal(null)
                result.name.should.be.equal("Dermalogica dynamic skin recovery SPF50 salon size 118ml")
                result.price.should.be.equal('83.50')
                result.url.should.be.equal("https://www.lemonskincare.com/collections/professional-products/products/dynamic-skin-recovery-spf50")
                result.inStock.should.be.equal(true)
                result.image.should.be.equal("https://cdn.shopify.com/s/files/1/1276/5567/products/Dermalogica_dynamic_skin_recovery_SPF50_salon_size_188ml_x480.JPG?v=1468843548")
                result.country.should.be.equal("uk")
                done()
            });
        })

    })
})



describe('Out of Stock Page', function () {

    var payload = {
        "pageType": "product",
        "url": "https://www.lemonskincare.com/products/precleanse-150ml",
        "html": cache.load("https://www.lemonskincare.com/products/precleanse-150ml")
    }

    it('should be true when product is out stock', function (done) {

        Structure(payload, function (err, inStockPage) {
            var expected = JSON.stringify({
                "name": "Dermalogica precleanse 150ml/5.1oz",
                "price": "27.85",
                "url": payload.url,
                "inStock": false,
                "stockLevel": null,
                "image": "https://cdn.shopify.com/s/files/1/1276/5567/products/1_x480.png?v=1462275714",
                "country": "uk"
            })
            JSON.stringify(inStockPage).should.equal(expected);
            done()
        })
    });

});

describe('Search Page', function () {

    var payload = {
        "pageType": "search",
        "url": "https://www.lemonskincare.com/collections/professional-products",
        "html": cache.load("https://www.lemonskincare.com/collections/professional-products")
    }

    // Change to be the number of products on the page
    var x = 36;
    it('should return ' + x + ' number of products', function (done) {
        Structure(payload, function (err, result) {
            result.length.should.equal(x);
            done()
        })
    });

    const firstProductName = 'Dermalogica active moist salon size 177ml/6oz';
    it('first product should be ' + firstProductName, function (done) {
        Structure(payload, function (err, result) {
            var first = result.shift()
            first.name.should.be.equal(firstProductName)
            first.price.should.be.equal("55.00")
            first.url.should.be.equal("https://www.lemonskincare.com/collections/professional-products/products/active-moist-177ml")
            first.image.should.be.equal("https://cdn.shopify.com/s/files/1/1276/5567/products/Dermalogica_active_moist_salon_size_177ml_x220.png?v=1468670525")
            first.country.should.be.equal("uk")
            done()
        })

    })

    var lastProductName = 'Dermalogica ultracalming serum concentrate salon size 118ml/4oz';
    it('last product should be ' + lastProductName, function (done) {
        Structure(payload, function (err, result) {
            var last = result.pop()
            var expected = JSON.stringify({
                barcode: null,
                name: lastProductName,
                price: '65.00',
                url: 'https://www.lemonskincare.com/collections/professional-products/products/ultracalming-serum-concentrate',
                image: 'https://cdn.shopify.com/s/files/1/1276/5567/products/Dermalogica_ultracalming_serum_concentrate_salon_size_118ml_cafb2b43-999b-49c9-97c3-bc25d2287c1e_x220.png?v=1469011696',
                country: 'uk'
            })

            JSON.stringify(last).should.equal(expected)
            done()
        });
    })

    describe('Search page category', function () {

        var payload = {
            "pageType": "category",
            "url": "https://www.lemonskincare.com/collections/professional-products",
            "html": cache.load("https://www.lemonskincare.com/collections/professional-products")
        }

        it('should be true when the category is correct', function (done) {

            Structure(payload, function (err, category) {
                var expected = JSON.stringify({
                    "name": "Professional products",
                    "url": payload.url
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });

});


