var should = require('should');
var Structure = require('../lib/structure.js')

describe('replaceWithwebAddress', function() {

    this.timeout(10000);

    describe('In Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : "https://www.lenox.com/elephant-dome-led-lamp/product/854646"
        }

        it('should be true when product is in stock', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "Elephant Dome LED Lamp by Lenox",
                    "price": "24.95",
                    "url": null,
                    "inStock": true,
                    "stockLevel": null,
                    "image": "https://lenox.scene7.com/is/image/Lenox/elephant-dome-led-lamp__854646_wHR",
                    "country": 'us',
                    "description": 'A fascinating montage of elephant life glows with a warm light from the Elephant Dome Lamp. Crafted of porcelain, this lovely dome lamp adds a unique lighted accent to any side table or mantle. Comes complete with an LED display.'
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });

    /*describe('Out of Stock Page', function() {

        var payload = {
            "pageType" : "product",
            "url" : ""
        }

        it('should be false when product is in store only', function (done) {

            Structure(payload, function(inStockPage) {
                var expected = JSON.stringify({
                    "name": "",
                    "price": "",
                    "url": null,
                    "inStock": false,
                    "stockLevel": null,
                    "image": '',
                    "country": ''
                })
                JSON.stringify(inStockPage).should.equal(expected);
                done()
            })
        });

    });*/

    describe('Search page pagination', function() {

        var payload = {
            "pageType" : "pagination",
            "url" : "https://www.lenox.com/searchresults?Ntt=table&No=0&Rdm=779&Nr=product.active:1&N=&Nrpp=96"
        }

        it('should be true when number of items and pages correct', function (done) {

            Structure(payload, function(pagination) {
                var expected = JSON.stringify({
                    "items": 96,
                    "pages": 48
                })
                JSON.stringify(pagination).should.equal(expected);
                done()
            })
        });

    })

    describe('Search page category', function() {

        var payload = {
            "pageType" : "category",
            "url" : "https://www.lenox.com/angels/category/30000123?N=561772708&Nrpp=24&No=0&Nr=product.active:1"
        }

        it('should be true when correct category is returned', function (done) {

            Structure(payload, function(category) {
                var expected = JSON.stringify({
                    "name": "Figurines"
                })
                JSON.stringify(category).should.equal(expected);
                done()
            })
        });

    });


    describe('Search Page', function() {

        var payload = {
            "pageType" : "search",
            "url" : "https://www.lenox.com/searchresults?Ntt=table&No=0&Rdm=779&Nr=product.active:1&N=&Nrpp=96"
        }

        // Change to be the number of products on the page
        var x = 96;
        it('should return ' + x + ' numer of products', function (done) {
            Structure(payload, function(result) {
                result.length.should.equal(x);
                done()
            })
        });

        var firstProductName = 'Elephant Dome LED Lamp';
        it('first product should be ' + firstProductName, function(done) {
            Structure(payload, function(result) {
                var first = result.shift()
                var expected = JSON.stringify({
                    barcode: null,
                    name: firstProductName,
                    price: '24.95',
                    url: 'https://www.lenox.com/elephant-dome-led-lamp/product/854646',
                    image: 'https://lenox.scene7.com/is/image/Lenox/elephant-dome-led-lamp__854646_wHR',
                    country: 'us'
                })
                JSON.stringify(first).should.equal(expected)
                done()
            })

        })

        var lastProductName = 'French Perle White Bird Salt & Pepper Set';
        it('last product should be ' + lastProductName, function (done) {
            Structure(payload, function(result) {
                var last = result.pop()
                var expected = JSON.stringify({
                    barcode: null,
                    name: lastProductName,
                    price: '24.95',
                    url: 'https://www.lenox.com/french-perle-white-bird-salt-%26-pepper-set/product/833151',
                    image: 'https://lenox.scene7.com/is/image/Lenox/french-perle-white-bird-salt--pepper-set__833151_wHR',
                    country: 'us'
                })

                JSON.stringify(last).should.equal(expected)
                done()
            });
        })

    });

});
